SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_info_ult_reval ( nr_seq_cpoe_p bigint, ie_tipo_item_p text, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE



/*
P - Proxima revalidacao medica
D - Data revalidacao medica
M - Usuario revalidacao medica
*/
dt_atualizacao_nrec_w	timestamp;
cd_medico_w	cpoe_revalidation_events.cd_medico%type;
nr_seq_reval_rule_w		cpoe_revalidation_rule.nr_sequencia%type;
dt_prox_revalidacao_w	timestamp;


BEGIN

if (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '' AND ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') then

	nr_seq_reval_rule_w := cpoe_obter_seq_reval_rule(nr_seq_cpoe_p, ie_tipo_item_p);
	
	if ((nr_seq_reval_rule_w IS NOT NULL AND nr_seq_reval_rule_w::text <> '') and nr_seq_reval_rule_w > 0) then
		if (ie_retorno_p = 'P') then

			dt_prox_revalidacao_w := cpoe_obter_data_prox_reval(nr_seq_cpoe_p, ie_tipo_item_p);
		
			if (dt_prox_revalidacao_w IS NOT NULL AND dt_prox_revalidacao_w::text <> '') then
				return pkg_date_formaters.to_varchar(trunc(dt_prox_revalidacao_w,'mi'),'short', obter_estabelecimento_ativo, obter_usuario_ativo);
			else
				return null;
			end if;
		end if;

		if (ie_tipo_item_p = 'N') then
		
			select	max(dt_atualizacao_nrec),
				max(cd_medico)
			into STRICT	dt_atualizacao_nrec_w,
				cd_medico_w
			from 	cpoe_revalidation_events
			where	nr_seq_diet = nr_seq_cpoe_p;
			
		elsif (ie_tipo_item_p = 'H') then
			
			select	max(dt_atualizacao_nrec),
				max(cd_medico)
			into STRICT	dt_atualizacao_nrec_w,
				cd_medico_w
			from 	cpoe_revalidation_events
			where	nr_seq_hemotherapy = nr_seq_cpoe_p;
		
		elsif (ie_tipo_item_p = 'D' or ie_tipo_item_p = 'DI' or ie_tipo_item_p = 'DP') then
		
			select	max(dt_atualizacao_nrec),
				max(cd_medico)
			into STRICT	dt_atualizacao_nrec_w,
				cd_medico_w
			from 	cpoe_revalidation_events
			where	nr_seq_dialysis = nr_seq_cpoe_p;

		elsif (ie_tipo_item_p = 'G') then
		
			select	max(dt_atualizacao_nrec),
				max(cd_medico)
			into STRICT	dt_atualizacao_nrec_w,
				cd_medico_w
			from 	cpoe_revalidation_events
			where	nr_seq_gasotherapy = nr_seq_cpoe_p;
		
		elsif (ie_tipo_item_p = 'M' or ie_tipo_item_p = 'MA') then
		
			select	max(dt_atualizacao_nrec),
				max(cd_medico)
			into STRICT	dt_atualizacao_nrec_w,
				cd_medico_w
			from 	cpoe_revalidation_events
			where	nr_seq_material = nr_seq_cpoe_p;

		elsif (ie_tipo_item_p = 'R') then

			select	max(dt_atualizacao_nrec),
				max(cd_medico)
			into STRICT	dt_atualizacao_nrec_w,
				cd_medico_w
			from 	cpoe_revalidation_events
			where	nr_seq_recomendation = nr_seq_cpoe_p;
			
		elsif (ie_tipo_item_p = 'P') then

			select	max(dt_atualizacao_nrec),
				max(cd_medico)
			into STRICT	dt_atualizacao_nrec_w,
				cd_medico_w
			from 	cpoe_revalidation_events
			where	nr_seq_exam = nr_seq_cpoe_p;

		end if;


		if (ie_retorno_p = 'D') then
			return pkg_date_formaters.to_varchar(trunc(dt_atualizacao_nrec_w,'mi'),'short', obter_estabelecimento_ativo, obter_usuario_ativo);
		elsif (ie_retorno_p = 'M') then
			return cd_medico_w;
		end if;
	end if;

end if;

return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION cpoe_obter_info_ult_reval ( nr_seq_cpoe_p bigint, ie_tipo_item_p text, ie_retorno_p text) FROM PUBLIC;

