CREATE OR REPLACE FUNCTION cpoe_obter_inicio_prescr_copia ( itens_liberar_p text, nr_atendimento_p bigint, dt_referencia_p timestamp) RETURNS timestamp AS $body$
DECLARE



dt_retorno_w			timestamp;
itens_liberar_w			varchar(4000);
item_w					varchar(255);
ie_opcao_prescr_w		varchar(255);
nr_seq_reg_item_w		bigint;
BEGIN
itens_liberar_w :=  itens_liberar_p;

for i in 1..length(itens_liberar_w) loop
		if (length(itens_liberar_w) > 1) then
			item_w := substr(itens_liberar_w, 1, position(';' in itens_liberar_w) - 1);
			ie_opcao_prescr_w := substr(item_w, 1, position(',' in item_w) - 1);
			nr_seq_reg_item_w := (substr(item_w, position(',' in item_w) + 1, length(item_w)))::numeric;
			itens_liberar_w := substr(itens_liberar_w, position(';' in itens_liberar_w) + 1, length(itens_liberar_w));

			if (ie_opcao_prescr_w = 'M') then --Medicamento
					select	min(dt_horario)
					into STRICT	dt_retorno_w
					from	prescr_mat_hor
					where	nr_prescricao =	(	SELECT	coalesce(max(b.nr_prescricao),0)
												from	prescr_material a,
														prescr_medica b
												where   a.nr_prescricao = b.nr_prescricao
												and 	b.nr_atendimento = nr_atendimento_p
												and		nr_seq_mat_cpoe = nr_seq_reg_item_w);
/*
			elsif	(ie_opcao_prescr_w = 'N') then --Nutrição

				select	min(dt_horario)	--Oral
				into	dt_retorno_w
				from	prescr_dieta_hor
				where	nr_prescricao =	(	select	nvl(max(b.nr_prescricao),0)
											from	prescr_dieta a,
													prescr_medica b
											where   a.nr_prescricao = b.nr_prescricao
											and 	b.nr_atendimento = nr_atendimento_p
											and		nr_seq_mat_cpoe = nr_seq_reg_item_w);

				if	(dt_retorno_w is null) then

					select	min(dt_horario)	--Enteral
					into	dt_retorno_w
					from	prescr_mat_hor
					where	nr_prescricao =	(	select	nvl(max(b.nr_prescricao),0)
												from	prescr_material a,
														prescr_medica b
												where   a.nr_prescricao = b.nr_prescricao
												and 	b.nr_atendimento = nr_atendimento_p
												and		nr_seq_mat_cpoe = nr_seq_reg_item_w
												and		ie_agrupador = 8);
				end if;

				if	(dt_retorno_w is null) then

					select	min(dt_horario)	--Enteral
					into	dt_retorno_w
					from	prescr_dieta_hor
					where	nr_prescricao =	(	select	nvl(max(b.nr_prescricao),0)
												from	rep_jejum a,
														prescr_medica b
												where   a.nr_prescricao = b.nr_prescricao
												and 	b.nr_atendimento = nr_atendimento_p
												and		nr_seq_mat_cpoe = nr_seq_reg_item_w);

						select	nvl(min(b.nr_prescricao),0) --Jejum
						into	nr_pres_original_w
						from	rep_jejum a,
								prescr_medica b
						where   a.nr_prescricao = b.nr_prescricao
						and 	b.nr_atendimento = nr_atendimento_p
						and	nr_seq_dieta_cpoe = nr_sequencia_p;

				end if;

				if	(dt_retorno_w is null) then

						select	nvl(min(b.nr_prescricao),0) --Suplemento
						into	nr_pres_original_w
						from	prescr_material a,
								prescr_medica b
						where   a.nr_prescricao = b.nr_prescricao
						and 	b.nr_atendimento = nr_atendimento_p
						and		nr_seq_dieta_cpoe = nr_sequencia_p
						and		ie_agrupador = 12;

				end if;


				if	(dt_retorno_w is null) then

						select	nvl(min(b.nr_prescricao),0) --Leites e Derivados
						into	nr_pres_original_w
						from	prescr_leite_deriv a,
								prescr_medica b
						where   a.nr_prescricao = b.nr_prescricao
						and 	b.nr_atendimento = nr_atendimento_p
						and  	nr_seq_dieta_cpoe = nr_sequencia_p;

				end if;

				if	(dt_retorno_w is null) then

						select	nvl(min(b.nr_prescricao),0) --Parenteral Infantil
						into	nr_pres_original_w
						from	nut_pac a,
								prescr_medica b
						where   a.nr_prescricao = b.nr_prescricao
						and 	b.nr_atendimento = nr_atendimento_p
						and		nr_seq_npt_cpoe = nr_sequencia_p
						and 	ie_npt_adulta = 'P';

				end if;

				if	(dt_retorno_w is null) then

						select	nvl(min(b.nr_prescricao),0)	--Parenteral Adulto
						into	nr_pres_original_w
						from	nut_pac a,
								prescr_medica b
						where   a.nr_prescricao = b.nr_prescricao
						and 	b.nr_atendimento = nr_atendimento_p
						and		nr_seq_npt_cpoe = nr_sequencia_p
						and 	ie_npt_adulta = 'S';

				end if;


			elsif	(ie_opcao_prescr_w = 'R') then

				select	nvl(min(b.nr_prescricao),0)		--Recomendação
				into	nr_pres_original_w
				from	prescr_recomendacao a,
						prescr_medica b
				where   a.nr_prescricao = b.nr_prescricao
				and 	b.nr_atendimento = nr_atendimento_p
				and 	nr_seq_rec_cpoe = nr_sequencia_p;

			elsif	(ie_opcao_prescr_w = 'G') then --Gasoterapia

				select	nvl(min(b.nr_prescricao),0)
				into	nr_pres_original_w
				from	prescr_gasoterapia a,
						prescr_medica b
				where   a.nr_prescricao = b.nr_prescricao
				and 	b.nr_atendimento = nr_atendimento_p
				and	nr_seq_gas_cpoe = nr_sequencia_p;

			elsif	(ie_opcao_prescr_w = 'P') then --Procedimento

				select	nvl(min(b.nr_prescricao),0)
				into	nr_pres_original_w
				from	prescr_procedimento a,
						prescr_medica b
				where   a.nr_prescricao = b.nr_prescricao
				and 	b.nr_atendimento = nr_atendimento_p
				and	nr_seq_proc_cpoe = nr_sequencia_p;

			elsif	(ie_opcao_prescr_w = 'D') then --Diálise

				select	nvl(min(b.nr_prescricao),0)
				into	nr_pres_original_w
				from	hd_prescricao a,
						prescr_medica b
				where   a.nr_prescricao = b.nr_prescricao
				and 	b.nr_atendimento = nr_atendimento_p
				and	nr_seq_dialise_cpoe = nr_sequencia_p;
			*/
			end if;


		end if;
end loop;

if (dt_retorno_w IS NOT NULL AND dt_retorno_w::text <> '') then
	dt_retorno_w := to_date(to_char(dt_referencia_p,'dd/mm/yyyy ')|| to_char(dt_retorno_w, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');
else
	dt_retorno_w := dt_referencia_p;
end if;

return dt_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION cpoe_obter_inicio_prescr_copia ( itens_liberar_p text, nr_atendimento_p bigint, dt_referencia_p timestamp) FROM PUBLIC;

