CREATE OR REPLACE FUNCTION cpoe_obter_interv_habilita_hor (cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


ie_operacao_w		intervalo_prescricao.ie_operacao%type;
ds_horarios_w		intervalo_prescricao.ds_horarios%type;



BEGIN
if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then

	select	ie_operacao,
			ds_horarios
	into STRICT	ie_operacao_w,
			ds_horarios_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;

	if (ie_operacao_w = 'F') then
		return 'N';
	end if;

	if (ie_operacao_w = 'D' and (ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '')) then
		return 'N';
	end if;

end if;

return 'S';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
