CREATE OR REPLACE FUNCTION cpoe_obter_itens_liberar ( ds_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_campo_w    	varchar(3000);
i	     		integer;


BEGIN

if (ds_campo_p IS NOT NULL AND ds_campo_p::text <> '') then
  begin

  ds_campo_w := replace(ds_campo_w, ' ', '');

  for i in 1..length(ds_campo_p) loop
    begin
		if (substr(ds_campo_p, i, 1) in ('1','2','3','4','5','6','7','8','9','0',';')) then
			ds_campo_w := ds_campo_w || substr(ds_campo_p, i, 1);
		end if;
    end;
  end loop;

  end;
end if;

ds_campo_w := replace(ds_campo_w, ';', ',');

return ds_campo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
