SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_justificativa_item (nr_seq_cpoe_p bigint, cd_item_p bigint, ie_tipo_item_p text, ie_add_justif_item_p text default 'S') RETURNS varchar AS $body$
DECLARE


ie_duplicado_w			cpoe_justificativa_item.ie_duplicado%type;
ie_interacao_medic_w		cpoe_justificativa_item.ie_interacao_medic%type;
ie_nao_padronizado_w		cpoe_justificativa_item.ie_nao_padronizado%type;
ie_alergia_w			cpoe_justificativa_item.ie_alergia%type;
ie_intolerancia_w		cpoe_justificativa_item.ie_intolerancia%type;
ie_dose_limite_w		cpoe_justificativa_item.ie_dose_limite%type;
ie_via_nao_recomendada_w	cpoe_justificativa_item.ie_via_nao_recomendada%type;
ie_interacao_dieta_w		cpoe_justificativa_item.ie_interacao_dieta%type;
ie_permite_lactantes_w		cpoe_justificativa_item.ie_permite_lactante%type;
ie_dose_minima_w		cpoe_justificativa_item.ie_dose_minima%type;
ie_exame_lab_w			cpoe_justificativa_item.ie_exame_lab%type;
ie_especialidade_medica_w	cpoe_justificativa_item.ie_especialidade_medica%type;
ie_permite_dil_w		cpoe_justificativa_item.ie_permite_dil%type;
ie_latex_w			cpoe_justificativa_item.ie_latex%type;
ds_just_duplicado_w		cpoe_justificativa_item.ds_just_duplicado%type;
ds_just_interacao_medic_w	cpoe_justificativa_item.ds_just_interacao_medic%type;
ds_just_nao_padronizado_w	cpoe_justificativa_item.ds_just_nao_padronizado%type;
ds_just_alergia_w		cpoe_justificativa_item.ds_just_alergia%type;
ds_just_intolerancia_w		cpoe_justificativa_item.ds_just_intolerancia%type;
ds_just_dose_limite_w		cpoe_justificativa_item.ds_just_dose_limite%type;
ds_just_via_nao_recom_w		cpoe_justificativa_item.ds_just_via_nao_recom%type;
ds_just_interacao_dieta_w	cpoe_justificativa_item.ds_just_interacao_dieta%type;
ds_justificativa_lactante_w	cpoe_justificativa_item.ds_justificativa_lactante%type;
ds_just_dose_minima_w		cpoe_justificativa_item.ds_just_dose_minima%type;
ds_exame_lab_w			cpoe_justificativa_item.ds_exame_lab%type;
ds_just_especialidade_med_w	cpoe_justificativa_item.ds_just_especialidade_med%type;
ds_justificativa_dil_w		cpoe_justificativa_item.ds_justificativa_dil%type;
ds_just_latex_w			cpoe_justificativa_item.ds_just_latex%type;
ds_justificativa_medic_w	varchar(4000) := '';

C01 CURSOR FOR
	SELECT	coalesce(ie_duplicado,'N') ie_duplicado,
		coalesce(ie_interacao_medic,'N') ie_interacao_medic,
		coalesce(ie_nao_padronizado,'N') ie_nao_padronizado,
		coalesce(ie_alergia,'N') ie_alergia,
		coalesce(ie_intolerancia,'N') ie_intolerancia,
		coalesce(ie_dose_limite,'N') ie_dose_limite,
		coalesce(ie_via_nao_recomendada,'N') ie_via_nao_recomendada,
		coalesce(ie_interacao_dieta,'N') ie_interacao_dieta,
		coalesce(ie_permite_lactante,'N') ie_permite_lactante,
		coalesce(ie_dose_minima,'N') ie_dose_minima,
		coalesce(ie_exame_lab,'N') ie_exame_lab,
		coalesce(ie_especialidade_medica,'N') ie_especialidade_medica,
		coalesce(ie_permite_dil,'N') ie_permite_dil,
		coalesce(ie_latex,'N') ie_latex,
		ds_just_duplicado,
		ds_just_interacao_medic,
		ds_just_nao_padronizado,
		ds_just_alergia,
		ds_just_intolerancia,
		ds_just_dose_limite,
		ds_just_via_nao_recom,
		ds_just_interacao_dieta,
		ds_justificativa_lactante,
		ds_just_dose_minima,
		ds_exame_lab,
		ds_just_especialidade_med,
		ds_justificativa_dil,
		ds_just_latex,
		nr_seq_mat_interacao,
		ds_duplicado,
		ds_alergia,
		ds_intolerancia,
		ds_dose_limite
	from	cpoe_justificativa_item
	where	((nr_seq_procedimento 	= nr_seq_cpoe_p and ie_tipo_item_p = 'P')
	or (nr_seq_material 	= nr_seq_cpoe_p and ie_tipo_item_p = 'M')
	or (nr_seq_hemoterapia 	= nr_seq_cpoe_p and ie_tipo_item_p = 'H')
	or (nr_seq_gasoterapia 	= nr_seq_cpoe_p and ie_tipo_item_p = 'G')
	or (nr_seq_dieta 		= nr_seq_cpoe_p and ie_tipo_item_p = 'N')
	or (nr_seq_dialise 	= nr_seq_cpoe_p and ie_tipo_item_p = 'D'))
	and	coalesce(cd_item,0) 		= coalesce(cd_item_p,0);

	function getJustificativaItem return text is
		ds_justificativa_w			varchar(2000);
		nr_seq_adicional_w 			cpoe_material.nr_seq_adicional%type;
		nr_seq_ataque_w				cpoe_material.nr_seq_ataque%type;
	
BEGIN
		select	substr(max(ds_justificativa),1,2000),
				max(nr_seq_adicional),
				max(nr_seq_ataque)
		into STRICT	ds_justificativa_w,
				nr_seq_adicional_w,
				nr_seq_ataque_w
		from	cpoe_material
		where	nr_sequencia = nr_seq_cpoe_p;

		if (nr_seq_adicional_w IS NOT NULL AND nr_seq_adicional_w::text <> '') then

				select	substr(max(ds_justificativa),1,2000)
				into STRICT	ds_justificativa_w
				from	cpoe_material
				where	nr_sequencia = nr_seq_adicional_w;

			return ds_justificativa_w || chr(13);

		elsif (nr_seq_ataque_w IS NOT NULL AND nr_seq_ataque_w::text <> '') then

				select	substr(max(ds_justificativa),1,2000)
				into STRICT	ds_justificativa_w
				from	cpoe_material
				where	nr_sequencia = nr_seq_ataque_w;

			return ds_justificativa_w || chr(13);

		end if;


		if (ds_justificativa_w IS NOT NULL AND ds_justificativa_w::text <> '') then
			return ds_justificativa_w || chr(13);
		else
			return null;
		end if;
	end;

	function getDescription( ds_item_p varchar2, ie_add_item_p varchar2, ds_mensagem_p varchar2, cd_expression_p number) return varchar2 is
	begin
		if (coalesce(ie_add_item_p,'N') = 'S') then
			if (coalesce(cd_expression_p, 0) > 0) and (ds_item_p IS NOT NULL AND ds_item_p::text <> '') then
				if (ds_mensagem_p IS NOT NULL AND ds_mensagem_p::text <> '') then
					return obter_desc_expressao(cd_expression_p, '') || '[' || ds_mensagem_p || ']: ' || ds_item_p  || chr(13);
				else
					return obter_desc_expressao(cd_expression_p, '') || ': ' || ds_item_p  || chr(13);
				end if;
			end if;
		end if;

		return null;
	end;
	
	function get_mat_description(nr_seq_mat_interaction_p	number) return varchar2 is
	ds_material_w	varchar(2000);
	begin
		select 	substr(max(obter_desc_material(cd_material)),1,2000)
		into STRICT	ds_material_w
		from 	cpoe_material
		where 	nr_sequencia = nr_seq_mat_interaction_p;
		
		return ds_material_w;
	end;
	
	function getInteractionDecription( ds_item_p varchar2, ie_add_item_p varchar2, nr_seq_mat_interaction_p number) return varchar2 is
	description_w 	varchar(2000);
	begin
		if (coalesce(ie_add_item_p,'N') = 'S') then
			description_w := get_mat_description(nr_seq_mat_interaction_p);
			
			if (description_w IS NOT NULL AND description_w::text <> '') then
				return obter_desc_expressao(307566, '') || '[' || description_w || ']: ' || ds_item_p  || chr(13);
			end if;
		end if;

		return null;
	end;	

begin

for c01_w in C01  loop
	begin
	
	ds_just_interacao_medic_w	:= substr(ds_just_interacao_medic_w	|| getInteractionDecription(c01_w.ds_just_interacao_medic, 	c01_w.ie_interacao_medic, 	c01_w.nr_seq_mat_interacao), 1, 2000);

	ds_just_duplicado_w		:= substr(ds_just_duplicado_w 		|| getDescription(c01_w.ds_just_duplicado, 		c01_w.ie_duplicado, 		c01_w.ds_duplicado,	330826), 1, 2000);
	ds_just_nao_padronizado_w	:= substr(ds_just_nao_padronizado_w	|| getDescription(c01_w.ds_just_nao_padronizado, 	c01_w.ie_nao_padronizado, 	null,			331237), 1, 2000);
	ds_just_alergia_w		:= substr(ds_just_alergia_w 		|| getDescription(c01_w.ds_just_alergia, 		c01_w.ie_alergia, 		c01_w.ds_alergia,	283343), 1, 2000);
	ds_just_intolerancia_w		:= substr(ds_just_intolerancia_w	|| getDescription(c01_w.ds_just_intolerancia, 		c01_w.ie_intolerancia, 		c01_w.ds_intolerancia,	1068383), 1, 2000);
	ds_just_dose_limite_w		:= substr(ds_just_dose_limite_w		|| getDescription(c01_w.ds_just_dose_limite, 		c01_w.ie_dose_limite, 		c01_w.ds_dose_limite,	288246), 1, 2000);
	ds_just_via_nao_recom_w		:= substr(ds_just_via_nao_recom_w	|| getDescription(c01_w.ds_just_via_nao_recom, 		c01_w.ie_via_nao_recomendada, 	null,			654404), 1, 2000);
	ds_just_interacao_dieta_w	:= substr(ds_just_interacao_dieta_w	|| getDescription(c01_w.ds_just_interacao_dieta, 	c01_w.ie_interacao_dieta, 	null,			703190), 1, 2000);
	ds_justificativa_lactante_w	:= substr(ds_justificativa_lactante_w	|| getDescription(c01_w.ds_justificativa_lactante, 	c01_w.ie_permite_lactante, 	null,			594952), 1, 2000);
	ds_just_dose_minima_w		:= substr(ds_just_dose_minima_w		|| getDescription(c01_w.ds_just_dose_minima, 		c01_w.ie_dose_minima, 		null,			288254), 1, 2000);
	ds_exame_lab_w			:= substr(ds_exame_lab_w		|| getDescription(c01_w.ds_exame_lab, 			c01_w.ie_exame_lab, 		null,			297828), 1, 2000);
	ds_just_especialidade_med_w	:= substr(ds_just_especialidade_med_w	|| getDescription(c01_w.ds_just_especialidade_med,	c01_w.ie_especialidade_medica, 	null,			289437), 1, 2000);
	ds_justificativa_dil_w		:= substr(ds_justificativa_dil_w 	|| getDescription(c01_w.ds_justificativa_dil, 		c01_w.ie_permite_dil, 		null,			287942), 1, 2000);
	ds_just_latex_w			:= substr(ds_just_latex_w 		|| getDescription(c01_w.ds_just_latex, 			c01_w.ie_latex, 		null,			630709), 1, 2000);
	
	end;
end loop;

ds_justificativa_medic_w := '';
if (ie_tipo_item_p = 'M' and ie_add_justif_item_p = 'S')then
 ds_justificativa_medic_w := getJustificativaItem();
end if;

return	substr(	
		ds_justificativa_medic_w 	||
		ds_just_duplicado_w 		|| 
		ds_just_interacao_medic_w 	|| 
		ds_just_nao_padronizado_w 	|| 
		ds_just_alergia_w 		|| 
		ds_just_intolerancia_w		|| 
		ds_just_dose_limite_w 		|| 
		ds_just_via_nao_recom_w 	|| 
		ds_just_interacao_dieta_w 	|| 
		ds_justificativa_lactante_w	|| 
		ds_just_dose_minima_w 		|| 
		ds_exame_lab_w 			|| 
		ds_just_especialidade_med_w 	|| 
		ds_justificativa_dil_w 		|| 
		ds_just_latex_w
	,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION cpoe_obter_justificativa_item (nr_seq_cpoe_p bigint, cd_item_p bigint, ie_tipo_item_p text, ie_add_justif_item_p text default 'S') FROM PUBLIC;

