SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_nm_usuario_lib (nr_sequencia_p bigint, ie_opcao_p text, nr_atendimento_p bigint, dt_liberacao timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(100) := '';


BEGIN

if (coalesce(dt_liberacao::text, '') = '') then
    if (ie_opcao_p = 'M') then --Medicamento
		begin
		select	max(a.nm_usuario_liberacao)
		into STRICT	ds_retorno_w
		from	prescr_medica_compl a
		where	a.nr_prescricao = cpoe_obter_prescricao_original(nr_sequencia_p, 'M', nr_atendimento_p);
		exception
			when others then
				ds_retorno_w	:= '';
		end;
    end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
