CREATE OR REPLACE FUNCTION cpoe_obter_ordem_mat_sol ( cd_material_p cpoe_material.cd_material%type, ie_controle_tempo_p cpoe_material.ie_controle_tempo%type, ie_tipo_solucao_p cpoe_material.ie_tipo_solucao%type, ie_via_aplicacao_p cpoe_material.ie_via_aplicacao%type, cd_estabelecimento_p bigint, cd_perfil_p bigint, cd_setor_usuario_p bigint, nm_usuario_p text, ie_funcao_p text default 'C', cd_mat_comp1_p bigint default null, cd_mat_comp2_p bigint default null, cd_mat_comp3_p bigint default null, cd_mat_comp4_p bigint default null, cd_mat_comp5_p bigint default null, cd_mat_comp6_p bigint default null, cd_mat_comp7_p bigint default null) RETURNS bigint AS $body$
DECLARE


cd_material_w 					bigint;					
cd_grupo_material_w				bigint;
cd_subgrupo_material_w			bigint;
cd_classe_material_w			bigint;
					
ie_subgrupo_w				cpoe_regra_ordem_subgrupo.ie_subgrupo%type;
nr_seq_ordem_subgrupo_w		cpoe_regra_ordem_subgrupo.nr_sequencia%type;

nr_seq_apresentacao_w		cpoe_regra_ordem_itens.nr_seq_apresentacao%type;
nr_seq_apresent_aux_w		cpoe_regra_ordem_itens.nr_seq_apresentacao%type;

c01 CURSOR FOR
SELECT	c.nr_sequencia
from	cpoe_regra_ordem_lib_v a,
		cpoe_regra_ordem_grupo b,
		cpoe_regra_ordem_subgrupo c
where	a.nr_seq_regra_ordem = b.nr_seq_regra_ordem
and		c.nr_seq_ordem_grupo = b.nr_sequencia
and		b.ie_grupo = 'M'
and		c.ie_subgrupo = ie_subgrupo_w
and	((coalesce(a.cd_estabelecimento::text, '') = '') or (cd_estabelecimento_p = a.cd_estabelecimento))
and	((coalesce(a.cd_setor_atendimento::text, '') = '') or (cd_setor_usuario_p = a.cd_setor_atendimento))
and	((coalesce(a.nm_usuario_regra::text, '') = '') or (nm_usuario_p = a.nm_usuario_regra))
and	((coalesce(a.cd_perfil::text, '') = '') or (cd_perfil_p = a.cd_perfil))
and		coalesce(a.ie_situacao,'A') = 'A'
and (a.ie_funcao_cpoe = 'A' or a.ie_funcao_cpoe = coalesce(ie_funcao_p, 'C'))
order by	coalesce(a.nm_usuario_regra,'') desc,
			coalesce(a.cd_setor_atendimento,0),
			coalesce(a.cd_perfil,0),
			coalesce(a.cd_estabelecimento,0);
			
c02 CURSOR FOR
SELECT	nr_seq_apresentacao
from	cpoe_regra_ordem_itens
where	nr_seq_ordem_subgrupo = nr_seq_ordem_subgrupo_w
and		coalesce(cd_material,cd_material_w) = cd_material_w
and		coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w
and		coalesce(cd_subgrupo_material,cd_subgrupo_material_w) = cd_subgrupo_material_w
and		coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w
and	    coalesce(ie_tipo_solucao, coalesce(ie_tipo_solucao_p,'XPTO')) = coalesce(ie_tipo_solucao_p,'XPTO')
order	by 
		coalesce(cd_material,0),
		coalesce(cd_grupo_material,0),	
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_classe_material,0),
		coalesce(ie_tipo_solucao,'');

c03 CURSOR FOR
SELECT	nr_seq_apresentacao
from	cpoe_regra_ordem_itens
where	nr_seq_ordem_subgrupo = nr_seq_ordem_subgrupo_w
and		coalesce(cd_material,cd_material_p) = cd_material_p
and		coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w
and		coalesce(cd_subgrupo_material,cd_subgrupo_material_w) = cd_subgrupo_material_w
and		coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w
and		coalesce(ie_via_aplicacao, coalesce(ie_via_aplicacao_p,'XPTO')) = coalesce(ie_via_aplicacao_p,'XPTO')
order	by 
		coalesce(cd_material,0),
		coalesce(cd_grupo_material,0),	
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_classe_material,0),
		coalesce(ie_via_aplicacao,'');
		
c04 CURSOR FOR
SELECT coalesce(cd_material,0),
	   coalesce(cd_grupo_material,0),
	   coalesce(cd_subgrupo_material,0),
	   coalesce(cd_classe_material,0)
from estrutura_material_v
where cd_material in (cd_material_p, cd_mat_comp1_p, cd_mat_comp2_p, cd_mat_comp3_p,
                      cd_mat_comp4_p, cd_mat_comp5_p, cd_mat_comp6_p, cd_mat_comp7_p);
					

BEGIN

nr_seq_apresentacao_w := 9999;

if (ie_controle_tempo_p = 'S') then
	ie_subgrupo_w := 'SOL';
else
	ie_subgrupo_w := 'M';
end if;

open c01;
loop
fetch c01 into nr_seq_ordem_subgrupo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if (ie_subgrupo_w = 'SOL') then
	
	open c04;
	loop
	fetch c04 into 	cd_material_w,
					cd_grupo_material_w,
					cd_subgrupo_material_w,
					cd_classe_material_w;
	EXIT WHEN NOT FOUND; /* apply on c04 */

		open c02;
		loop
		fetch c02 into nr_seq_apresent_aux_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */

		if (coalesce(nr_seq_apresent_aux_w,9999) < nr_seq_apresentacao_w) then
			nr_seq_apresentacao_w := nr_seq_apresent_aux_w;
		end if;

		end loop;
		close c02;

	end loop;
	close c04;
	
elsif (ie_subgrupo_w = 'M') then
	
	select	coalesce(max(b.cd_grupo_material),0),
			coalesce(max(b.cd_subgrupo_material),0),
			coalesce(max(b.cd_classe_material),0)
	into STRICT	cd_grupo_material_w,
			cd_subgrupo_material_w,
			cd_classe_material_w
	from	estrutura_material_v b
	where 	b.cd_material = cd_material_p;	
	
	open c03;
	loop
	fetch c03 into nr_seq_apresentacao_w;
	EXIT WHEN NOT FOUND; /* apply on c03 */
	end loop;
	close c03;
	
end if;

return nr_seq_apresentacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
