SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_ordem_medica ( nr_seq_cpoe_p bigint, param_614_p text default null, dt_liberacao_p timestamp default null, cd_estabelecimento_p bigint default null, nm_usuario_p text default null, ie_controle_tempo_p text default null) RETURNS varchar AS $body$
DECLARE


ds_ordem_w 			prescr_medica_ordem.ds_ordem%Type;
ds_ordem_ret_w 		varchar(2000) := '';
nr_prescricao_w 	prescr_material.nr_prescricao%TYPE;
nr_seq_med_w 		prescr_material.nr_prescricao%TYPE;
nr_seq_sol_w 		prescr_material.nr_prescricao%TYPE;
ds_tipo_ordem_w 	ordem_medica.ds_ordem%Type;
dt_ordem_w			prescr_medica_ordem.dt_ordem%Type;
dt_realizacao_w		prescr_medica_ordem.dt_realizacao%Type;


C01 CURSOR FOR
	SELECT 	b.ds_ordem,
			a.ds_ordem,
			b.dt_ordem,
			b.dt_realizacao
	from	ordem_medica a,
			prescr_medica_ordem b
	where 	b.nr_seq_ordem = a.nr_sequencia
	and 	b.nr_prescricao = nr_prescricao_w
	and 	b.nr_seq_material = nr_seq_med_w
	order by b.nr_sequencia desc;

C02 CURSOR FOR
	SELECT 	b.ds_ordem,
			a.ds_ordem,
			b.dt_ordem,
			b.dt_realizacao
	from	ordem_medica a,
			prescr_medica_ordem b
	where 	b.nr_seq_ordem = a.nr_sequencia
	and 	b.nr_prescricao = nr_prescricao_w
	and 	b.nr_seq_solucao = nr_seq_sol_w
	order by b.nr_sequencia desc;


BEGIN

if (dt_liberacao_p IS NOT NULL AND dt_liberacao_p::text <> '') then

	if (coalesce(ie_controle_tempo_p,'N') = 'N') then --Medicamento
		select 	max(nr_sequencia),
				max(nr_prescricao)
		into STRICT 	nr_seq_med_w,
				nr_prescricao_w
		from 	prescr_material
		where 	nr_seq_mat_cpoe = nr_seq_cpoe_p;

	elsif (coalesce(ie_controle_tempo_p,'N') = 'S') then --Solução
		select 	max(a.nr_seq_solucao),
				max(b.nr_prescricao)
		into STRICT 	nr_seq_sol_w,
				nr_prescricao_w
		from 	prescr_solucao a,
				prescr_material b
		where 	a.nr_prescricao = b.nr_prescricao
		and 	a.nr_seq_solucao = b.nr_sequencia_solucao
		and 	b.nr_seq_mat_cpoe = nr_seq_cpoe_p;

	end if;

	if (coalesce(ie_controle_tempo_p,'N') = 'S') then
		open 	C02;
		loop
		fetch 	C02 into
				ds_ordem_w,
				ds_tipo_ordem_w,
				dt_ordem_w,
				dt_realizacao_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			if (coalesce(ds_ordem_ret_w,'X') != 'X') then
				ds_ordem_ret_w := substr(ds_ordem_ret_w || chr(13),1,2000);
			end if;

			ds_ordem_ret_w := substr(ds_ordem_ret_w
						|| pkg_date_formaters.to_varchar(dt_ordem_w,'timestamp',cd_estabelecimento_p,nm_usuario_p)
						|| ' - ' || ds_tipo_ordem_w || chr(13),1,2000);

			if (coalesce(ds_ordem_w,'X') != 'X') then
				ds_ordem_ret_w := substr(ds_ordem_ret_w || ds_ordem_w || chr(13),1,2000);
			end if;

			if (dt_realizacao_w IS NOT NULL AND dt_realizacao_w::text <> '') then
				ds_ordem_ret_w := substr(ds_ordem_ret_w || Wheb_mensagem_pck.get_texto(712003, 'dt_exec='
				|| pkg_date_formaters.to_varchar(dt_realizacao_w,'timestamp',cd_estabelecimento_p,nm_usuario_p))
				|| chr(13),1,2000);
			else
				ds_ordem_ret_w := substr(ds_ordem_ret_w || Wheb_mensagem_pck.get_texto(711997) || chr(13),1,2000);
			end if;
			end;
		end loop;
		close C02;
	else
		open 	C01;
		loop
		fetch 	C01 into
				ds_ordem_w,
				ds_tipo_ordem_w,
				dt_ordem_w,
				dt_realizacao_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			if (coalesce(ds_ordem_ret_w,'X') != 'X') then
				ds_ordem_ret_w := substr(ds_ordem_ret_w || chr(13),1,2000);
			end if;

			ds_ordem_ret_w := substr(ds_ordem_ret_w
						|| pkg_date_formaters.to_varchar(dt_ordem_w,'timestamp',cd_estabelecimento_p,nm_usuario_p)
						|| ' - ' || ds_tipo_ordem_w || chr(13),1,2000);

			if (coalesce(ds_ordem_w,'X') != 'X') then
				ds_ordem_ret_w := substr(ds_ordem_ret_w || ds_ordem_w || chr(13),1,2000);
			end if;

			if (dt_realizacao_w IS NOT NULL AND dt_realizacao_w::text <> '') then
				ds_ordem_ret_w := substr(ds_ordem_ret_w || Wheb_mensagem_pck.get_texto(712003, 'dt_exec='
				|| pkg_date_formaters.to_varchar(dt_realizacao_w,'timestamp',cd_estabelecimento_p,nm_usuario_p))
				|| chr(13),1,2000);
			else
				ds_ordem_ret_w := substr(ds_ordem_ret_w || Wheb_mensagem_pck.get_texto(711997) || chr(13),1,2000);
			end if;


			end;
		end loop;
		close C01;
	end if;
end if;

return ds_ordem_ret_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
