CREATE OR REPLACE FUNCTION cpoe_obter_param_rel_gf ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);
nr_seq_cpoe_w	cpoe_material.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	cpoe_material
	where	((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = ''))
	and 	((cpoe_reg_valido_ativacao(CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  coalesce(dt_fim_cih,dt_fim)  ELSE cpoe_obter_data_maior(dt_suspensao,dt_fim) END ,dt_inicio, clock_timestamp(), cd_funcao_origem, obter_perfil_ativo) = 'S') or (ie_retrogrado = 'S' and coalesce(dt_liberacao::text, '') = ''))
	and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and 	coalesce(ie_material,'N') = 'N';

