	from	cpoe_dialise
	where	((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = ''))
	and 	((cpoe_reg_valido_ativacao( CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE cpoe_obter_data_maior(dt_suspensao,dt_fim) END , dt_inicio, clock_timestamp(), null, obter_perfil_ativo) = 'S') or (ie_retrogrado = 'S' and coalesce(dt_liberacao::text, '') = ''))
	and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

C07 CURSOR FOR
	SELECT	nr_sequencia
	from	cpoe_material
	where	((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = ''))
