	where	((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = ''))
	and 	((cpoe_reg_valido_ativacao( CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE cpoe_obter_data_maior(dt_suspensao,dt_fim) END , dt_inicio, clock_timestamp(), cd_funcao_origem, obter_perfil_ativo) = 'S') or (ie_retrogrado = 'S' and coalesce(dt_liberacao::text, '') = ''))
	and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');


BEGIN

if (ie_tipo_item_p = 'M') then

	open C01;
	loop
	fetch C01 into
		nr_seq_cpoe_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		ds_retorno_w := nr_seq_cpoe_w || ', ' || ds_retorno_w;

		end;
	end loop;
	close C01;
end if;

