	EXIT WHEN NOT FOUND; /* apply on C08 */
		begin

		ds_retorno_w := nr_seq_cpoe_w || ', ' || ds_retorno_w;

		end;
	end loop;
	close C08;
end if;

return coalesce(ds_retorno_w, 'N');

end;
$body$
