CREATE OR REPLACE FUNCTION cpoe_obter_posicao_acm ( ie_administracao_p text, cd_estabelecimento_p bigint, cd_perfil_p bigint, cd_setor_usuario_p bigint, nm_usuario_p text, ie_grupo_p text, ie_funcao_p text default 'C') RETURNS bigint AS $body$
DECLARE

					
nr_sequencia_w			cpoe_regra_ordenacao.nr_sequencia%type;
nr_seq_apresentacao_w	cpoe_regra_ordem_grupo.nr_seq_apresentacao%type;
ie_acm_sn_final_w		cpoe_regra_ordem_grupo.ie_acm_sn_final%type;

c01 CURSOR FOR
SELECT	a.nr_seq_regra_ordem
from	cpoe_regra_ordem_lib_v a
where	((coalesce(a.cd_estabelecimento::text, '') = '') or (cd_estabelecimento_p = a.cd_estabelecimento))
and	((coalesce(a.cd_setor_atendimento::text, '') = '') or (cd_setor_usuario_p = a.cd_setor_atendimento))
and	((coalesce(a.nm_usuario_regra::text, '') = '') or (nm_usuario_p = a.nm_usuario_regra))
and	((coalesce(a.cd_perfil::text, '') = '') or (cd_perfil_p = a.cd_perfil))
and		coalesce(a.ie_situacao,'A') = 'A'
and (a.ie_funcao_cpoe = 'A' or a.ie_funcao_cpoe = ie_funcao_p)
order by	coalesce(a.nm_usuario_regra,'') desc,
			coalesce(a.cd_setor_atendimento,0),
			coalesce(a.cd_perfil,0),
			coalesce(a.cd_estabelecimento,0);

BEGIN

open c01;
loop
fetch c01 into	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

select	coalesce(max(ie_acm_sn_final),'N')
into STRICT	ie_acm_sn_final_w
from	cpoe_regra_ordem_grupo
where	nr_seq_regra_ordem = nr_sequencia_w
and		ie_grupo = ie_grupo_p;

if (coalesce(ie_administracao_p,'P') <> 'P') and (ie_acm_sn_final_w = 'S') then
	nr_seq_apresentacao_w := 9999999;
else
	nr_seq_apresentacao_w := 0;
end if;

return nr_seq_apresentacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
