SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_prescr_horario (nr_sequencia_p bigint, ie_tipo_item_p text, nr_atendimento_p bigint, nr_seq_horario_p bigint) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w		prescr_medica.nr_prescricao%type;


BEGIN

if (ie_tipo_item_p		= 'D') then

		select	max(a.nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_medica a,
				prescr_dieta b,
				prescr_dieta_hor c
		where	a.nr_prescricao	= b.nr_prescricao
		and		a.nr_prescricao = c.nr_prescricao
		and		a.nr_atendimento = nr_atendimento_p
		and		b.nr_seq_dieta_cpoe	= nr_sequencia_p
		and		c.nr_sequencia = nr_seq_horario_p;

elsif (ie_tipo_item_p		in ('SNE','S','LD')) then

		select	max(a.nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_medica a,
				prescr_material b,
				prescr_mat_hor c
		where	a.nr_prescricao	= b.nr_prescricao
		and		a.nr_prescricao = c.nr_prescricao
		and		a.nr_atendimento = nr_atendimento_p
		and		b.nr_seq_dieta_cpoe	= nr_sequencia_p
		and		c.nr_sequencia = nr_seq_horario_p;

elsif (ie_tipo_item_p		= 'J') then

		select	max(a.nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_medica a,
				rep_jejum b
		where	a.nr_prescricao	= b.nr_prescricao
		and		a.nr_atendimento = nr_atendimento_p
		and		b.nr_seq_dieta_cpoe	= nr_sequencia_p;

elsif (ie_tipo_item_p		in ('M','SOL','MAT')) then

		select	max(a.nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_medica a,
				prescr_material b,
				prescr_mat_hor c
		where	a.nr_prescricao	= b.nr_prescricao
		and		a.nr_prescricao = c.nr_prescricao
		and		a.nr_atendimento = nr_atendimento_p
		and		b.nr_seq_mat_cpoe = nr_sequencia_p
		and		c.nr_sequencia = nr_seq_horario_p;

elsif (ie_tipo_item_p		in ('P','HM','AP')) then

		select	max(a.nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_medica a,
				prescr_procedimento b,
				prescr_proc_hor c
		where	a.nr_prescricao	= b.nr_prescricao
		and		a.nr_prescricao = c.nr_prescricao
		and		a.nr_atendimento = nr_atendimento_p
		and		b.nr_seq_proc_cpoe = nr_sequencia_p
		and		c.nr_sequencia = nr_seq_horario_p;

elsif (ie_tipo_item_p		= 'O') then

		select	max(a.nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_medica a,
				prescr_gasoterapia b,
				prescr_gasoterapia_hor c
		where	a.nr_prescricao	= b.nr_prescricao
		and		a.nr_prescricao = c.nr_prescricao
		and		a.nr_atendimento = nr_atendimento_p
		and		b.nr_seq_gas_cpoe = nr_sequencia_p
		and		c.nr_sequencia = nr_seq_horario_p;

elsif (ie_tipo_item_p		= 'R') then

		select	max(a.nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_medica a,
				prescr_recomendacao b,
				prescr_rec_hor c
		where	a.nr_prescricao	= b.nr_prescricao
		and		a.nr_prescricao = c.nr_prescricao
		and		a.nr_atendimento =nr_atendimento_p
		and		b.nr_seq_rec_cpoe = nr_sequencia_p
		and		c.nr_sequencia = nr_seq_horario_p;

elsif (ie_tipo_item_p		= 'DI') then

		select	max(a.nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_medica a,
				prescr_solucao b
		where	a.nr_prescricao	= b.nr_prescricao
		and		a.nr_atendimento = nr_atendimento_p
		and		b.nr_seq_dialise_cpoe	= nr_sequencia_p;

end if;

return	nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
