CREATE OR REPLACE FUNCTION cpoe_obter_prime_dialise_rel ( ie_ligar_prime_p text, qt_volume_prime_p bigint, qt_sodio_p bigint, qt_bicarbonato_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);

	function ligar_prime return text is
	;
BEGIN
		return '                ' || obter_desc_expressao(292572, null) || ': '|| obter_valor_dominio(6,ie_ligar_prime_p) ||' ';
	end;

	function volume_prime return varchar2 is
	begin
		return ' ' || obter_desc_expressao(302274, null) || ': '|| qt_volume_prime_p;
	end;

begin
--Inserido espaços pois futuramente relatório wate que usa isto se tornará um cate onde será melhorado isso.
	if (coalesce(qt_sodio_p,0) > 0) then
		ds_retorno_w := ds_retorno_w || substr(obter_desc_expressao(298623, null) || ': '|| trim(both to_char(coalesce(qt_sodio_p,0),'99999G999G990D09')),1,255) || '	           ';
	end if;

	if (coalesce(qt_bicarbonato_p,0) > 0) then
		ds_retorno_w := ds_retorno_w || substr(obter_desc_expressao(284301, null) || ': '|| trim(both to_char(coalesce(qt_bicarbonato_p,0),'99999G999G990D09')),1,255) || '';
	end if;

	ds_retorno_w := ds_retorno_w || ligar_prime();

	if (ie_ligar_prime_p = 'S') then
		ds_retorno_w := ds_retorno_w || '	          ' ||  volume_prime();
	end if;

	return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
