CREATE OR REPLACE FUNCTION cpoe_obter_prim_comp_sol (cd_protocolo_p bigint, nr_seq_protocolo_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_comp_w		bigint;
ds_componente_w	varchar(100);


BEGIN
if (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then

	select	min(b.nr_seq_material)
	into STRICT	nr_seq_comp_w
	from 	protocolo_medic_solucao a,
			protocolo_medic_material b
	where	a.cd_protocolo = b.cd_protocolo
	and		a.nr_sequencia = b.nr_sequencia
	and 	a.nr_seq_solucao = b.nr_seq_solucao
	and		a.cd_protocolo = cd_protocolo_p
	and 	a.nr_sequencia = nr_seq_protocolo_p
	and 	a.nr_seq_solucao = nr_seq_solucao_p;

	select	substr(coalesce(b.ds_abrev_solucao,obter_desc_material(a.cd_material)),1,100)
	into STRICT	ds_componente_w
	from	protocolo_medic_material a,
			material b
	where	a.cd_material	= b.cd_material
	and		a.cd_protocolo 		= cd_protocolo_p
	and		a.nr_sequencia 		= nr_seq_protocolo_p
	and 	a.nr_seq_material 	= nr_seq_comp_w;

end if;

return substr(ds_componente_w,1,240);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
