CREATE OR REPLACE FUNCTION cpoe_obter_prot_glic_regra ( nr_seq_protocolo_p bigint, cd_perfil_p bigint, nm_usuario_p text, ie_opcao_p text, cd_estabelecimento_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_utiliza_protocolo_w	varchar(5) := 'S';
ie_permite_edicao_w		varchar(5) := 'S';
cd_medico_w				varchar(50);
cont_w					bigint;
cd_setor_atendimento_w	pep_prot_glic_regra.cd_setor_atendimento%type;
	
c01 CURSOR FOR 
SELECT	coalesce(ie_utiliza_protocolo,'S'), 
		coalesce(ie_permite_edicao,'S') 
from	pep_prot_glic_regra 
where	nr_seq_protocolo_glic	= nr_seq_protocolo_p 
and		((cd_perfil = cd_perfil_p) or (coalesce(cd_perfil::text, '') = '')) 
and		((nm_usuario_regra = nm_usuario_p) or (coalesce(nm_usuario_regra::text, '') = '')) 
and		((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_medico_w, cd_especialidade) = 'S')) 
and		((coalesce(cd_estab::text, '') = '') or (cd_estab = cd_estabelecimento_p)) 
and		((cd_setor_atendimento = cd_setor_atendimento_w) or (coalesce(cd_setor_atendimento::text, '') = '')) 
and		((coalesce(nr_seq_agrupamento::text, '') = '') or (nr_seq_agrupamento = Obter_agrupamento_setor(cd_setor_atendimento_w))) 
order by 
	cd_perfil, 
	nm_usuario_regra;


BEGIN 
 
cd_setor_atendimento_w := cpoe_obter_setor_atend_prescr(nr_atendimento_p, cd_estabelecimento_p, cd_perfil_p, nm_usuario_p);
 
select	count(*) 
into STRICT	cont_w 
from	pep_prot_glic_regra where		nr_seq_protocolo_glic	= nr_seq_protocolo_p LIMIT 1;
 
if (cont_w > 0) then 
	ie_utiliza_protocolo_w	:= 'N';
	ie_permite_edicao_w	:= 'N';
	 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_medico_w 
	from	usuario 
	where	nm_usuario	= nm_usuario_p;
	 
	open C01;
	loop 
	fetch C01 into	 
		ie_utiliza_protocolo_w, 
		ie_permite_edicao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ie_utiliza_protocolo_w	:= ie_utiliza_protocolo_w;
		ie_permite_edicao_w		:= ie_permite_edicao_w;
	end loop;
	close C01;
end if;
 
if (ie_opcao_p	= 'E') then 
	return	coalesce(ie_permite_edicao_w,'S');
elsif (ie_opcao_p	= 'U') then 
	return	coalesce(ie_utiliza_protocolo_w,'S');
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
