CREATE OR REPLACE FUNCTION cpoe_obter_qt_prox_dia_semana ( cd_intervalo_p text) RETURNS bigint AS $body$
DECLARE


qt_proximo_dia_w	bigint := 0;
dt_inicio_item_w	timestamp := clock_timestamp();

ie_operacao_w		intervalo_prescricao.ie_operacao%type;
ds_horarios_w		intervalo_prescricao.ds_horarios%type;


BEGIN
if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then
	select 	ie_operacao,
			ds_horarios
	into STRICT	ie_operacao_w,
			ds_horarios_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;

	if (ie_operacao_w = 'D') then
		if (ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') then
			dt_inicio_item_w := to_date(to_char(clock_timestamp(), 'dd/mm/yyyy ') || obter_prim_DsHorarios(ds_horarios_w), 'dd/mm/yyyy hh24:mi');
			if (dt_inicio_item_w < clock_timestamp()) then
				dt_inicio_item_w := dt_inicio_item_w + 1;
			end if;
		end if;

		qt_proximo_dia_w := TRUNC(obter_proxima_dose_medic(dt_inicio_item_w, cd_intervalo_p, ds_horarios_w, 'N')) - TRUNC(clock_timestamp());
	end if;
end if;

return qt_proximo_dia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
