CREATE OR REPLACE FUNCTION cpoe_obter_quant_horas_ant ( cd_perfil_p perfil.cd_perfil%type) RETURNS bigint AS $body$
DECLARE


qt_horas_ant_w	cpoe_presentation_rule.qt_horas_anteriores%type;
qt_horas_default_w	cpoe_presentation_rule.qt_horas_anteriores%type := 6;
					

BEGIN

if (obter_funcao_ativa <> 7010) then
	CALL wheb_usuario_pck.set_cd_funcao(2314);
end if;

CALL wheb_usuario_pck.set_ds_form('AtePac_OE');

select	coalesce(max(qt_horas_anteriores),qt_horas_default_w)
into STRICT	qt_horas_ant_w
from (SELECT qt_horas_anteriores
	from	cpoe_presentation_rule
	where 	coalesce(cd_perfil, coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0)
	and	coalesce(cd_estabelecimento, coalesce(wheb_usuario_pck.get_cd_estabelecimento,0)) = coalesce(wheb_usuario_pck.get_cd_estabelecimento,0)
	order by cd_perfil, cd_estabelecimento) alias8 LIMIT 1;

if (qt_horas_ant_w < 2) then
	qt_horas_ant_w := 2;
elsif (qt_horas_ant_w > qt_horas_default_w) then
	qt_horas_ant_w := qt_horas_default_w;
end if;

return qt_horas_ant_w;

end;
$body$
