CREATE OR REPLACE FUNCTION cpoe_obter_se_atb_vigente ( nr_prescricao_p bigint, nr_seq_material_w bigint, dt_inicio_validade_p timestamp, dt_fim_validade_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';


BEGIN

	if ((nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') and (dt_inicio_validade_p IS NOT NULL AND dt_inicio_validade_p::text <> '') and (dt_fim_validade_p IS NOT NULL AND dt_fim_validade_p::text <> '')) then

		select	coalesce(max('S'), 'N')
		into STRICT	ds_retorno_w
		from	prescr_mat_hor
		where	nr_prescricao = nr_prescricao_p
		and		nr_seq_material = nr_seq_material_w
		and		dt_horario between dt_inicio_validade_p and dt_fim_validade_p  LIMIT 1;
	end if;
	
return ds_retorno_w;

end;
$body$
