CREATE OR REPLACE FUNCTION cpoe_obter_se_exibe_interv ( nr_atendimento_p bigint, cd_estabelecimento_p bigint, cd_intervalo_p text, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_exibe_w					varchar(1) := 'S';
cd_setor_atendimento_w		integer;
cd_prescritor_w				varchar(10);

c01 CURSOR FOR
SELECT	coalesce(ie_exibir,'N')
from	setor_intervalo
where	(((coalesce(ie_setor,'P') = 'P') and (cd_setor_atendimento = cd_setor_atendimento_w)) or
		 ((coalesce(ie_setor,'P') = 'E') and
		  ((coalesce(cd_setor_atendimento_w,0) = 0) or (cd_setor_atendimento	= cd_setor_atendimento_w))) or (coalesce(cd_setor_atendimento::text, '') = ''))
and		((cd_prescritor_w = cd_pessoa_fisica) or (coalesce(cd_pessoa_fisica::text, '') = ''))
and		((cd_perfil_p = cd_perfil) or (coalesce(cd_perfil::text, '') = ''))
and		((coalesce(cd_estab::text, '') = '') or (cd_estab = cd_estabelecimento_p))
and		cd_intervalo		= cd_intervalo_p
order by
		cd_pessoa_fisica desc,
		cd_setor_atendimento desc,
		cd_perfil desc;


BEGIN

if (nr_atendimento_p > 0) then

	select	coalesce(max('N'),'S')
	into STRICT	ie_exibe_w
	from	setor_intervalo
	where	cd_intervalo = cd_intervalo_p;

	if (ie_exibe_w = 'N') then

		cd_setor_atendimento_w := cpoe_obter_setor_atend_prescr(nr_atendimento_p, cd_estabelecimento_p, cd_perfil_p, nm_usuario_p);

		select	max(a.cd_pessoa_fisica)
		into STRICT	cd_prescritor_w
		from	usuario a
		where	a.nm_usuario_pesquisa = upper(nm_usuario_p);

		ie_exibe_w := 'S';
		open C01;
		loop
		fetch C01 into
			ie_exibe_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ie_exibe_w	:= ie_exibe_w;
			end;
		end loop;
		close C01;

	end if;
end if;

return ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
