CREATE OR REPLACE FUNCTION cpoe_obter_se_exibe_protocolo ( nr_seq_protocolo_p bigint, ie_tipo_peritoneal_p text, ie_destino_p bigint, ie_tipo_peritoneal_cpoe_p text) RETURNS varchar AS $body$
DECLARE


ie_exibe_w				varchar(1) := 'N';
ie_tipo_peritoneal_w	varchar(15);
cont_w					integer;


BEGIN

ie_tipo_peritoneal_w := ie_tipo_peritoneal_cpoe_p;

	if (ie_tipo_peritoneal_w IS NOT NULL AND ie_tipo_peritoneal_w::text <> '') and (ie_destino_p = 3) and
		((ie_tipo_peritoneal_w = ie_tipo_peritoneal_p) or (coalesce(ie_tipo_peritoneal_p::text, '') = '')) then
		ie_exibe_w	:= 'S';
	elsif (coalesce(ie_tipo_peritoneal_w::text, '') = '') and (ie_destino_p = 2) then
		ie_exibe_w	:= 'S';
	end if;

	if (ie_exibe_w	= 'N') then
		select	count(*)
		into STRICT	cont_w
		from	tipo_dialise_solucao
		where	nr_seq_Protocolo_npt	= nr_seq_protocolo_p;

		if (cont_w	> 0) then
			select	count(*)
			into STRICT	cont_w
			from	tipo_dialise_solucao
			where	nr_seq_Protocolo_npt	= nr_seq_protocolo_p
			and	ie_tipo_peritoneal	= ie_tipo_peritoneal_w;

			if (cont_w	> 0) then
				ie_exibe_w	:= 'S';
			end if;
		end if;
	end if;

return ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
