CREATE OR REPLACE FUNCTION cpoe_obter_se_hor_dif_val ( dt_horario_p timestamp, nr_sequencia_p bigint, ie_tipo_item_p text ) RETURNS varchar AS $body$
DECLARE


ie_segunda_w		cpoe_material.ie_segunda%type;
ie_terca_w		  cpoe_material.ie_terca%type;
ie_quarta_w		  cpoe_material.ie_quarta%type;
ie_quinta_w		  cpoe_material.ie_quinta%type;
ie_sexta_w		  cpoe_material.ie_sexta%type;
ie_sabado_w		  cpoe_material.ie_sabado%type;
ie_domingo_w		cpoe_material.ie_domingo%type;
ie_dia_semana_w		varchar(1);
ie_situacao_w		  varchar(1);


BEGIN

  select	coalesce(max(ie_segunda), 'S'),
          coalesce(max(ie_terca), 'S'),
          coalesce(max(ie_quarta), 'S'),
          coalesce(max(ie_quinta), 'S'),
          coalesce(max(ie_sexta), 'S'),
          coalesce(max(ie_sabado), 'S'),
          coalesce(max(ie_domingo), 'S')
  into STRICT	ie_segunda_w,
        ie_terca_w,
        ie_quarta_w,
        ie_quinta_w,
        ie_sexta_w,
        ie_sabado_w,
        ie_domingo_w
  from	cpoe_material
  where	nr_sequencia	= nr_sequencia_p;

  select	pkg_date_utils.get_WeekDay(dt_horario_p)
  into STRICT	ie_dia_semana_w
;

  if	(ie_dia_semana_w	= 1 AND ie_domingo_w		= 'S') or
    (ie_dia_semana_w	= 2 AND ie_segunda_w		= 'S') or
    (ie_dia_semana_w	= 3 AND ie_terca_w		= 'S') or
    (ie_dia_semana_w	= 4 AND ie_quarta_w		= 'S') or
    (ie_dia_semana_w	= 5 AND ie_quinta_w		= 'S') or
    (ie_dia_semana_w	= 6 AND ie_sexta_w		= 'S') or
    (ie_dia_semana_w	= 7 AND ie_sabado_w		= 'S') then
    ie_situacao_w := 'A';
  else
    ie_situacao_w := 'I';
  end if;

  return ie_situacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
