CREATE OR REPLACE FUNCTION cpoe_obter_se_incio_menor_lib ( nr_seq_item_cpoe_p bigint, ie_tipo_item_p text, dt_referencia_p timestamp default null) RETURNS varchar AS $body$
DECLARE

						
dt_inicio_w				timestamp;
hr_prim_horario_w		varchar(10);
ie_libera_w				varchar(1) := 'S';
ie_administracao_w		varchar(15);
ie_retrogrado_w			varchar(1);
ie_exists_integration_w	varchar(1);
ie_tipo_dieta_w			cpoe_dieta.ie_tipo_dieta%type;


BEGIN
if (ie_tipo_item_p = 'M') then

	select  max(dt_inicio),
			max(hr_prim_horario),
			max(ie_administracao),
			max(coalesce(ie_retrogrado,'N'))
	into STRICT    dt_inicio_w,
			hr_prim_horario_w,
			ie_administracao_w,
			ie_retrogrado_w
	from    cpoe_material
	where   nr_sequencia = nr_seq_item_cpoe_p;
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_exists_integration_w
	from	cpoe_integracao
	where	nr_seq_mat_cpoe = nr_seq_item_cpoe_p;

	if (ie_administracao_w = 'P') and (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') then
		dt_inicio_w := to_date(to_char(dt_inicio_w,'dd/mm/yyyy')||hr_prim_horario_w,'dd/mm/yyyy hh24:mi');
		if (dt_inicio_w < coalesce(dt_referencia_p, trunc(clock_timestamp(), 'mi'))) then
			ie_libera_w := 'N';
		end if;
	elsif (ie_administracao_w in ('C','N')) and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') and (trunc(dt_inicio_w) < trunc(clock_timestamp())) then
			ie_libera_w := 'N';
	end if;

elsif (ie_tipo_item_p = 'N') then --Nutricao
	select  max(dt_inicio),
			max(hr_prim_horario),
			max(ie_administracao),
			max(ie_tipo_dieta),
			max(coalesce(ie_retrogrado,'N'))
	into STRICT    dt_inicio_w,
			hr_prim_horario_w,
			ie_administracao_w,
			ie_tipo_dieta_w,
			ie_retrogrado_w
	from    cpoe_dieta
	where   nr_sequencia = nr_seq_item_cpoe_p;
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_exists_integration_w
	from	cpoe_integracao
	where	nr_seq_diet_cpoe = nr_seq_item_cpoe_p;

	if (ie_administracao_w = 'P') and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') then
	
		if (ie_tipo_dieta_w <> 'J') and (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') then
			dt_inicio_w := to_date(to_char(dt_inicio_w,'dd/mm/yyyy')||hr_prim_horario_w,'dd/mm/yyyy hh24:mi');
			if (dt_inicio_w < coalesce(dt_referencia_p, trunc(clock_timestamp(), 'mi'))) then
				ie_libera_w := 'N';
			end if;
		else
			if (dt_inicio_w < coalesce(dt_referencia_p, trunc(clock_timestamp(), 'mi'))) then
				ie_libera_w := 'N';
			end if;
		end if;				
	elsif (ie_administracao_w in ('C','N')) and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') and (trunc(dt_inicio_w) < trunc(clock_timestamp())) then
			ie_libera_w := 'N';
	end if;
	
elsif (ie_tipo_item_p = 'P') then
	select  max(dt_inicio),
			max(hr_prim_horario),
			max(ie_administracao),
			max(coalesce(ie_retrogrado,'N'))
	into STRICT    dt_inicio_w,
			hr_prim_horario_w,
			ie_administracao_w,
			ie_retrogrado_w
	from    cpoe_procedimento
	where   nr_sequencia = nr_seq_item_cpoe_p;
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_exists_integration_w
	from	cpoe_integracao
	where	nr_seq_proc_cpoe = nr_seq_item_cpoe_p;
	
	if (ie_administracao_w = 'P') and (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') then
		dt_inicio_w := to_date(to_char(dt_inicio_w,'dd/mm/yyyy')||hr_prim_horario_w,'dd/mm/yyyy hh24:mi');
		if (dt_inicio_w < coalesce(dt_referencia_p, trunc(clock_timestamp(), 'mi'))) then
			ie_libera_w := 'N';
		end if;
	elsif (ie_administracao_w in ('C','N')) and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') and (trunc(dt_inicio_w) < trunc(clock_timestamp())) then
			ie_libera_w := 'N';
	end if;

elsif (ie_tipo_item_p = 'G') then

	select  max(dt_inicio),
			max(hr_prim_horario),
			max(ie_administracao),
			max(coalesce(ie_retrogrado,'N'))
	into STRICT    dt_inicio_w,
			hr_prim_horario_w,
			ie_administracao_w,
			ie_retrogrado_w
	from    cpoe_gasoterapia
	where   nr_sequencia = nr_seq_item_cpoe_p;
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_exists_integration_w
	from	cpoe_integracao
	where	nr_seq_gaso_cpoe = nr_seq_item_cpoe_p;
	
	if (ie_administracao_w = 'P') and (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') then
		dt_inicio_w := to_date(to_char(dt_inicio_w,'dd/mm/yyyy')||hr_prim_horario_w,'dd/mm/yyyy hh24:mi');
		if (dt_inicio_w < coalesce(dt_referencia_p, trunc(clock_timestamp(), 'mi'))) then
			ie_libera_w := 'N';
		end if;
	elsif (ie_administracao_w in ('C','N')) and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') and (trunc(dt_inicio_w) < trunc(clock_timestamp())) then
			ie_libera_w := 'N';
	end if;

elsif (ie_tipo_item_p = 'R') then --Recomendacao
	select  max(dt_inicio),
			max(hr_prim_horario),
			max(ie_administracao),
			max(coalesce(ie_retrogrado,'N'))
	into STRICT    dt_inicio_w,
			hr_prim_horario_w,
			ie_administracao_w,
			ie_retrogrado_w
	from    cpoe_recomendacao
	where   nr_sequencia = nr_seq_item_cpoe_p;
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_exists_integration_w
	from	cpoe_integracao
	where	nr_seq_rec_cpoe = nr_seq_item_cpoe_p;
	
	if (ie_administracao_w = 'P') and (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') then
		dt_inicio_w := to_date(to_char(dt_inicio_w,'dd/mm/yyyy')||hr_prim_horario_w,'dd/mm/yyyy hh24:mi');
		if (dt_inicio_w < coalesce(dt_referencia_p, trunc(clock_timestamp(), 'mi'))) then
			ie_libera_w := 'N';
		end if;
	elsif (ie_administracao_w in ('C','N')) and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') and (trunc(dt_inicio_w) < trunc(clock_timestamp())) then
			ie_libera_w := 'N';
	end if;

elsif (ie_tipo_item_p = 'H') then --Hemoterapia
	select  max(dt_programada),
			max(coalesce(ie_retrogrado,'N'))
	into STRICT 	dt_inicio_w,
			ie_retrogrado_w
	from    cpoe_hemoterapia
	where   nr_sequencia = nr_seq_item_cpoe_p;
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_exists_integration_w
	from	cpoe_integracao
	where	nr_seq_hemo_cpoe = nr_seq_item_cpoe_p;

	if (dt_inicio_w IS NOT NULL AND dt_inicio_w::text <> '') and (ie_retrogrado_w = 'N') and (ie_exists_integration_w = 'N') and (dt_inicio_w < coalesce(dt_referencia_p, trunc(clock_timestamp(), 'mi'))) then
			ie_libera_w := 'N';
	end if;

end if;

return ie_libera_w;

end;
$body$
