CREATE OR REPLACE FUNCTION cpoe_obter_se_integra_fleury ( nr_prescricao_p prescr_medica.nr_prescricao%type, ds_method_p text default null) RETURNS varchar AS $body$
DECLARE


	nr_evento_convenio_w	        CONSTANT evento_convenio.nr_seq_evento%type := 106;

	ie_retorno_w			varchar(1);


BEGIN
	
	select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  ie_ev_convenio
	  into STRICT ie_retorno_w
	  from evento_convenio a,
		   lab_parametro b
	 where a.nr_seq_evento = nr_evento_convenio_w
	   and a.cd_estabelecimento = b.cd_estabelecimento
	   and coalesce(b.ie_integra_fleury, 'N') = 'S'
	   and coalesce(b.ie_int_fleury_manual, 'N') = 'N'
	   and b.cd_estabelecimento = obter_estabelecimento_ativo;
	
	/* Similar ao isFleuryActive no front */

	if ((ie_retorno_w = 'S') and (obter_se_int_prescr_fleury(nr_prescricao_p, ds_method_p) <> '0')) then
		ie_retorno_w := 'S';
	else
		ie_retorno_w := 'N';
	end if;
	
	return ie_retorno_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
