CREATE OR REPLACE FUNCTION cpoe_obter_se_item_valido_lib ( nr_seq_item_cpoe_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


qt_dose_w					cpoe_material.qt_dose%type;
ds_dose_diferenciada_w		cpoe_material.ds_dose_diferenciada%type;
ie_libera_w					varchar(1) := 'S';


BEGIN
if (ie_tipo_item_p = 'M') then

	select  max(qt_dose),
			max(ds_dose_diferenciada)
	into STRICT    qt_dose_w,
			ds_dose_diferenciada_w
	from    cpoe_material
	where   nr_sequencia = nr_seq_item_cpoe_p;

	if (coalesce(qt_dose_w,0) = 0) and (coalesce(ds_dose_diferenciada_w::text, '') = '') then
		ie_libera_w := 'N';
	end if;
end if;

return ie_libera_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
