CREATE OR REPLACE FUNCTION cpoe_obter_se_lib_parcial (nr_prescricao_p bigint, nr_seq_cpoe_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ie_prescr_lib_parcial_w	char(1);
ie_retorno_w	char(1) := 'N';


BEGIN
if ((nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '') and (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '')) then
	select	coalesce(max('S'), 'N')
	into STRICT	ie_prescr_lib_parcial_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p
	and	coalesce(dt_liberacao_farmacia::text, '') = ''
	and	(dt_liberacao_parc_farm IS NOT NULL AND dt_liberacao_parc_farm::text <> '');

	if (ie_prescr_lib_parcial_w = 'S') then
		if (ie_tipo_item_p in ('M', 'SOL')) then

			select	coalesce(max('S'), 'N')
			into STRICT	ie_prescr_lib_parcial_w
			from	prescr_material_incon_farm
			where	nr_prescricao = nr_prescricao_p
			and	coalesce(ie_situacao, 'A') = 'A';
			
			if (ie_prescr_lib_parcial_w = 'S') then
				select	coalesce(max('S'), 'N')
				into STRICT	ie_retorno_w
				from	cpoe_material
				where	nr_sequencia = nr_seq_cpoe_p
				and	(dt_liberacao_farm IS NOT NULL AND dt_liberacao_farm::text <> '');
			end if;
		elsif (ie_tipo_item_p = 'G') then

			select	coalesce(max('S'), 'N')
			into STRICT	ie_prescr_lib_parcial_w
			from	prescr_gasoterapia
			where	(nr_seq_inconsistencia IS NOT NULL AND nr_seq_inconsistencia::text <> '')
			and	nr_prescricao = nr_prescricao_p;
			
			if (ie_prescr_lib_parcial_w = 'S') then
				select	coalesce(max('S'), 'N')
				into STRICT	ie_retorno_w
				from	cpoe_gasoterapia
				where	nr_sequencia = nr_seq_cpoe_p
				and	(dt_liberacao_farm IS NOT NULL AND dt_liberacao_farm::text <> '');	
			end if;
		end if;
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
