CREATE OR REPLACE FUNCTION cpoe_obter_se_lib_tudo_proc (nr_seq_proc_cpoe_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE

				
ie_liberar_tudo_w		varchar(1) := 'N';
nr_seq_proc_interno_w	cpoe_procedimento.nr_seq_proc_interno%type;
ie_tipo_atendimento_w	atendimento_paciente.ie_tipo_atendimento%type;
cd_setor_atendimento_w	cpoe_procedimento.cd_setor_atendimento%type;
				
c01 CURSOR FOR
SELECT	ie_liberar_tudo
from	regra_proced_liberacao
where	((coalesce(nr_seq_proc_interno::text, '') = '') or (nr_seq_proc_interno = nr_seq_proc_interno_w))
and		((coalesce(ie_tipo_atendimento::text, '') = '') or (ie_tipo_atendimento = ie_tipo_atendimento_w))
and		((coalesce(cd_setor_atendimento::text, '') = '') or (cd_setor_atendimento = cd_setor_atendimento_w))
order by coalesce(NR_SEQ_PRIORIDADE,999) desc,
	 cd_area_procedimento,
	 cd_especialidade,
	 cd_grupo_proc,
	 cd_procedimento;
	

BEGIN

if (ie_tipo_item_p = 'P') then
	
	select 	max(nr_seq_proc_interno),
			obter_tipo_atendimento(max(nr_atendimento)),
			max(cd_setor_atendimento)
	into STRICT	nr_seq_proc_interno_w,
			ie_tipo_atendimento_w,
			cd_setor_atendimento_w
	from 	cpoe_procedimento
	where	nr_sequencia = nr_seq_proc_cpoe_p;
else
	
	select 	max(nr_seq_proc_interno),
			obter_tipo_atendimento(max(nr_atendimento)),
			max(cd_setor_atendimento)
	into STRICT	nr_seq_proc_interno_w,
			ie_tipo_atendimento_w,
			cd_setor_atendimento_w
	from 	cpoe_anatomia_patologica
	where	nr_sequencia = nr_seq_proc_cpoe_p;
end if;
	
open C01;
loop
fetch C01 into	
	ie_liberar_tudo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_liberar_tudo_w	:= ie_liberar_tudo_w;
end loop;
close C01;

return	ie_liberar_tudo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
