CREATE OR REPLACE FUNCTION cpoe_obter_se_mat_tag ( cd_material_p text, ie_tag_p text) RETURNS varchar AS $body$
DECLARE

qt_reg_w			bigint;
cd_grupo_material_w		bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;


BEGIN

begin
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material	= cd_material_p;
exception
when others then
	cd_grupo_material_w:= null;
	cd_subgrupo_material_w:= null;
	cd_classe_material_w:= null;
end;

begin
	select	count(1)
	into STRICT	qt_reg_w
	from	cpoe_tag_rule a
	where	a.ie_tag	= ie_tag_p
	and	coalesce(a.cd_material, cd_material_p)			= cd_material_p
	and	coalesce(a.cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
	and	coalesce(a.cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and	coalesce(a.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w;

	if (qt_reg_w	> 0) then
		return 'S';
	end if;
exception
when others then
	null;
end;

return	'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
