CREATE OR REPLACE FUNCTION cpoe_obter_se_mostra_item_prot ( nr_atendimento_p bigint, nr_seq_item_prot_p bigint, cd_protocolo_p bigint, nr_seq_protocolo_p bigint, ds_sigla_p text, ie_tipo_dieta_p text, ie_tipo_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_perfil_p bigint, cd_convenio_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_paciente_p text) RETURNS varchar AS $body$
DECLARE

						 
ie_retorno_w		varchar(1);
						

BEGIN 
 
ie_retorno_w := 'S';
 
if (ds_sigla_p = 'S') then --- Soluções 
 
 
	select	coalesce(max('N'),'S') 
	into STRICT	ie_retorno_w 
	from	protocolo_medic_solucao a, 
			protocolo_medic_material b 
	where	a.cd_protocolo = b.cd_protocolo 
	and		a.nr_sequencia = b.nr_sequencia 
	and		a.nr_seq_solucao = b.nr_seq_solucao 
	and		a.nr_seq_solucao = nr_seq_item_prot_p 
	and		a.cd_protocolo = cd_protocolo_p 
	and		a.nr_sequencia = nr_seq_protocolo_p 
	and		cpoe_obter_se_medic_lib_med(nr_atendimento_p, b.cd_material, 'N', a.ie_via_aplicacao, ie_tipo_atendimento_p, cd_setor_atendimento_p, cd_perfil_p, cd_convenio_p, nm_usuario_p, cd_estabelecimento_p, cd_paciente_p, clock_timestamp()) = 'N';
 
elsif (ds_sigla_p = 'M') or 
		 ((ds_sigla_p = 'N') and (ie_tipo_dieta_p in ('E','S')))then --- Medicamentos, Dieta Enteral, Dieta Oral 
 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_retorno_w 
	from	protocolo_medic_material a 
	where	a.nr_seq_material = nr_seq_item_prot_p 
	and		a.cd_protocolo = cd_protocolo_p 
	and		a.nr_sequencia = nr_seq_protocolo_p 
	and		cpoe_obter_se_medic_lib_med(nr_atendimento_p, a.cd_material, 'N', a.ie_via_aplicacao, ie_tipo_atendimento_p, cd_setor_atendimento_p, cd_perfil_p, cd_convenio_p, nm_usuario_p, cd_estabelecimento_p, cd_paciente_p, clock_timestamp()) = 'S' 
	and		((ds_sigla_p <> 'M') or (cpoe_obter_se_mat_plano_conv( nr_atendimento_p, a.cd_material, cd_setor_atendimento_p, cd_convenio_p, cd_estabelecimento_p) <> 'CAI'));
	 
elsif (ds_sigla_p = 'P') then --- Procedimentos/Exames 
 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_retorno_w 
	from	protocolo_medic_proc a 
	where	a.nr_seq_proc = nr_seq_item_prot_p 
	and		a.cd_protocolo = cd_protocolo_p 
	and		a.nr_sequencia = nr_seq_protocolo_p 
	and		cpoe_obter_se_proc_lib_rot(nr_atendimento_p, a.nr_seq_proc_interno, cd_setor_atendimento_p, nm_usuario_p, cd_perfil_p) = 'S';
 
end if;
 
return coalesce(ie_retorno_w,'S');
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
