DECLARE


result_w	char(1);


BEGIN

select coalesce(max('S'),'N')
into STRICT	result_w
from 	prescr_medica a, prescr_recomendacao b
where  	a.nr_prescricao = b.nr_prescricao
and  	b.nr_seq_rec_cpoe = nr_sequencia_cpoe_p
