CREATE OR REPLACE FUNCTION cpoe_obter_se_regra_dupli_just (ie_tipo_item_p text, cd_perfil_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

				
ds_retorno_w		varchar(1) := 'S';
ie_existe_regra_w	varchar(1);
nr_seq_regra_w		bigint;


BEGIN

select	max(b.nr_sequencia)
into STRICT	nr_seq_regra_w
from	cpoe_regra_consistencia c,
		cpoe_regra_duplicidade b
where	c.nr_sequencia = b.nr_seq_regra
and		coalesce(c.cd_perfil, cd_perfil_p) = cd_perfil_p
and		coalesce(c.cd_setor_atendimento, cd_setor_atendimento_p) = cd_setor_atendimento_p;

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
  if (ie_tipo_item_p in ('MAT','MCOMP1', 'MCOMP2', 'MCOMP3','MCOMP4', 'MCOMP5', 'MCOMP6')) then

    select ie_consiste_dupl_sol
    into STRICT 	ds_retorno_w
    from	cpoe_regra_duplicidade b
    where	b.nr_sequencia = nr_seq_regra_w;

  elsif (ie_tipo_item_p = 'P') then

    select ie_consiste_dupl_exa
    into STRICT 	ds_retorno_w
    from	cpoe_regra_duplicidade b
    where	b.nr_sequencia = nr_seq_regra_w;

  elsif (ie_tipo_item_p in ('E','J','L','O','S')) then

    select ie_consiste_dupl_nut
    into STRICT 	ds_retorno_w
    from	cpoe_regra_duplicidade b
    where	b.nr_sequencia = nr_seq_regra_w;

  elsif (ie_tipo_item_p = 'G') then

    select ie_consiste_dupl_gas
    into STRICT 	ds_retorno_w
    from	cpoe_regra_duplicidade b
    where	b.nr_sequencia = nr_seq_regra_w;

  elsif (ie_tipo_item_p = 'R') then

    select ie_consiste_dupl_rec
    into STRICT 	ds_retorno_w
    from	cpoe_regra_duplicidade b
    where	b.nr_sequencia = nr_seq_regra_w;

  elsif (ie_tipo_item_p in ('HBC', 'HP')) then

    select ie_consiste_dupl_hem
    into STRICT 	ds_retorno_w
    from	cpoe_regra_duplicidade b
    where	b.nr_sequencia = nr_seq_regra_w;

  elsif (ie_tipo_item_p in ('DI', 'DP')) then

    select ie_consiste_dupl_dia
    into STRICT 	ds_retorno_w
    from	cpoe_regra_duplicidade b
    where	b.nr_sequencia = nr_seq_regra_w;

  elsif (ie_tipo_item_p = 'M') then

    select ie_consiste_dupl_mat
    into STRICT 	ds_retorno_w
    from	cpoe_regra_duplicidade b
    where	b.nr_sequencia = nr_seq_regra_w;

  elsif (ie_tipo_item_p = 'AP') then

    select ie_consiste_dupl_ana
    into STRICT 	ds_retorno_w
    from	cpoe_regra_duplicidade b
    where	b.nr_sequencia = nr_seq_regra_w;

  end if;

else
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
