CREATE OR REPLACE FUNCTION cpoe_obter_se_regra_popup ( cd_perfil_p bigint, cd_setor_atendimento_p bigint, nr_atendimento_p cpoe_procedimento.nr_atendimento%type, nr_seq_proc_interno_p cpoe_procedimento.nr_seq_proc_interno%type, dt_inicio_p cpoe_procedimento.dt_inicio%type) RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w		varchar(4000) 	:= null;
ie_tem_regra_w		varchar(1) 	:= 'N';	

C01 CURSOR FOR
SELECT 	ie_consiste_dupl_exa
from  	cpoe_regra_consistencia c,
	cpoe_regra_duplicidade b
where  	c.nr_sequencia = b.nr_seq_regra
and (coalesce(c.cd_perfil::text, '') = '' or coalesce(c.cd_perfil, cd_perfil_p) = cd_perfil_p)
and (coalesce(c.cd_setor_atendimento::text, '') = '' or coalesce(c.cd_setor_atendimento, cd_setor_atendimento_p) = cd_setor_atendimento_p);
r_regra C01%rowtype;


BEGIN

open C01;
loop
	fetch C01
	into r_regra;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	if (r_regra.ie_consiste_dupl_exa = 'P') then
		ie_tem_regra_w := 'S';
		exit;
	end if;		
end loop;
close C01;

if (ie_tem_regra_w = 'S' and (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') and (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '')) then	
	select 	max(PKG_DATE_FORMATERS.to_varchar(DT_INICIO, 'shortDate', obter_estabelecimento_ativo, obter_usuario_ativo)||
		' '||substr(cpoe_obter_desc_proc_interno(nr_seq_proc_interno),1,255))
	into STRICT 	ds_retorno_w
	from 	CPOE_PROCEDIMENTO
	where 	nr_atendimento = nr_atendimento_p 
	and 	nr_seq_proc_interno = nr_seq_proc_interno_p
	and    	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
    and 	to_date(dt_inicio_p, pkg_date_formaters.localize_mask('timestamp', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario))) <= dt_inicio;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
