CREATE OR REPLACE FUNCTION cpoe_obter_se_soma_vol ( nr_seq_npt_cpoe_p cpoe_dieta.nr_sequencia%type, nr_seq_cpoe_prod_p cpoe_npt_produto.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_somar_volume_w	nut_elemento.ie_somar_volume%type;

BEGIN

select	coalesce(c.ie_somar_volume,'X')
into STRICT	ie_somar_volume_w
from	cpoe_npt_produto c
where	c.nr_sequencia		= nr_seq_cpoe_prod_p
and	c.nr_seq_npt_cpoe	= nr_seq_npt_cpoe_p;

if (ie_somar_volume_w = 'X') then
	select	coalesce(max(c.ie_somar_volume),'S')
	into STRICT	ie_somar_volume_w
	from	cpoe_npt_produto a,
		cpoe_npt_elemento b,
		nut_elemento c
	where	a.nr_seq_elemento = b.nr_seq_elemento
	and	a.nr_seq_npt_cpoe = b.nr_seq_npt_cpoe
	and	b.nr_seq_elemento = c.nr_sequencia
	and	a.nr_seq_npt_cpoe = nr_seq_npt_cpoe_p
	and	a.nr_sequencia = nr_seq_cpoe_prod_p;
end if;

return ie_somar_volume_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
