CREATE OR REPLACE FUNCTION cpoe_obter_se_suspenso_rel (nr_sequencia_p bigint, ie_tipo_item_p text, dt_liberacao_p timestamp default null, dt_fim_p timestamp default null) RETURNS varchar AS $body$
DECLARE

dt_suspensao_w	timestamp;


BEGIN 
 
if (coalesce(nr_sequencia_p,0) > 0) then 
 
	dt_suspensao_w := cpoe_obter_dt_suspensao(nr_sequencia_p,ie_tipo_item_p);
	 
	if (dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') then 
		 
		if (dt_liberacao_p IS NOT NULL AND dt_liberacao_p::text <> '') then --vindo da liberação	 
			return 'S';
		 
		elsif ((coalesce(dt_liberacao_p::text, '') = '') and (dt_suspensao_w > dt_fim_p)) then 
				return 'N';
		else 
			return 'S';
		end if;	
	 
	end if;
 
end if;
 
return	'N';
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
