CREATE OR REPLACE FUNCTION cpoe_obter_se_valid_prescr (nr_sequencia_p bigint, ie_tipo_item_p text, dt_inicio_p timestamp, nr_atendimento_p bigint default null, cd_pessoa_fisica_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';
nr_prescricao_w			prescr_medica.nr_prescricao%type;
dt_inicio_prescr_w		prescr_medica.dt_inicio_prescr%type;
dt_validade_prescr_w	prescr_medica.dt_validade_prescr%type;


BEGIN

if ((nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and	(ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '')) then

	nr_prescricao_w := obter_prescr_item_cpoe(nr_sequencia_p, ie_tipo_item_p, nr_atendimento_p, cd_pessoa_fisica_p);

	if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then

		select	dt_inicio_prescr,
				dt_validade_prescr
		into STRICT	dt_inicio_prescr_w,
				dt_validade_prescr_w
		from	prescr_medica
		where	nr_prescricao = nr_prescricao_w;

		if (dt_inicio_p between dt_inicio_prescr_w and dt_validade_prescr_w) then
			return 'S';
		else
			return 'N';
		end if;

	end if;
end if;



return	ds_retorno_w;

end;
