CREATE OR REPLACE FUNCTION cpoe_obter_se_via_adm ( cd_material_p bigint, ie_via_aplicacao_p text, nr_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w			varchar(1) := 'S';
cd_setor_atendimento_w	prescr_medica.cd_setor_atendimento%type;


BEGIN 
 
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then 
	cd_setor_atendimento_w := coalesce(obter_unidade_atendimento(nr_atendimento_p,'IA','CS'), obter_unidade_atendimento(nr_atendimento_p,'A','CS'));
	select	coalesce(max('N'), 'S') 
	into STRICT	ie_retorno_w 
	from	mat_via_aplic where		cd_material	= cd_material_p 
	and		coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_p,1))	= coalesce(cd_estabelecimento_p,1) LIMIT 1;
 
	if (ie_retorno_w = 'N') then 
		begin 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_retorno_w 
		from	material 
		where	cd_material	= cd_material_p 
		and		ie_via_aplicacao = ie_via_aplicacao_p;
 
		if (ie_retorno_w = 'N') then 
						 
			select	coalesce(max('S'),'N') 
			into STRICT	ie_retorno_w 
			from	mat_via_aplic where		cd_material	= cd_material_p 
			and		ie_via_aplicacao = ie_via_aplicacao_p 
			and		coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_p,1))	= coalesce(cd_estabelecimento_p,1) 
			and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_w,1))	= coalesce(cd_setor_atendimento_w,1) 
			and		coalesce(cd_setor_excluir,0) <> coalesce(cd_setor_atendimento_w,1) LIMIT 1;
		end if;	
		end;
	end if;
end if;
 
return ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
