CREATE OR REPLACE FUNCTION cpoe_obter_se_via_hemoc_setor ( ie_via_aplicacao_p text, cd_setor_atendimento_p bigint, nr_seq_derivado_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1) := 'S';


BEGIN

select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_permite_w
from	san_via_aplicacao
where	nr_seq_derivado = nr_seq_derivado_p;

	if ( ie_permite_w = 'N') then

		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	san_via_aplicacao
		where	nr_seq_derivado		= nr_seq_derivado_p
		and	ie_via_aplicacao	= ie_via_aplicacao_p
		and	((coalesce(cd_setor_atendimento::text, '') = '') or (cd_setor_atendimento = cd_setor_atendimento_p));

	end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
