CREATE OR REPLACE FUNCTION cpoe_obter_tempo_prescrito (dt_parametro_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(50);


BEGIN

ds_retorno_w := TO_CHAR(dt_parametro_p,'dd') || ' ';

ds_retorno_w := ds_retorno_w || Obter_desc_mes_data(dt_parametro_p,'D') || ' ';

ds_retorno_w := ds_retorno_w || wheb_mensagem_pck.get_texto(315172, 'NR_DIAS=' ||  (TRUNC(clock_timestamp()) - TRUNC(dt_parametro_p)));

return	ds_retorno_w;

end;
