CREATE OR REPLACE FUNCTION cpoe_obter_termino_item (nr_sequencia_p bigint, ie_tipo_item_p text) RETURNS timestamp AS $body$
DECLARE


dt_termino_w		timestamp;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	if (ie_tipo_item_p = 'N') then

		select	coalesce(cpoe_obter_dt_suspensao(nr_sequencia, 'N'), dt_fim)
		into STRICT	dt_termino_w
		from	cpoe_dieta
		where	nr_sequencia = nr_sequencia_p;

	elsif (ie_tipo_item_p = 'M') then

		select	coalesce(cpoe_obter_dt_suspensao(nr_sequencia, 'M'), coalesce(dt_fim_cih, dt_fim))
		into STRICT	dt_termino_w
		from	cpoe_material
		where	nr_sequencia = nr_sequencia_p;

	elsif (ie_tipo_item_p = 'P') then

		select	coalesce(cpoe_obter_dt_suspensao(nr_sequencia, 'P'), dt_fim)
		into STRICT	dt_termino_w
		from	cpoe_procedimento
		where	nr_sequencia = nr_sequencia_p;

	elsif (ie_tipo_item_p = 'G') then

		select	coalesce(cpoe_obter_dt_suspensao(nr_sequencia, 'G'), dt_fim)
		into STRICT	dt_termino_w
		from	cpoe_gasoterapia
		where	nr_sequencia = nr_sequencia_p;

	elsif (ie_tipo_item_p = 'R') then

		select	coalesce(cpoe_obter_dt_suspensao(nr_sequencia, 'R'), dt_fim)
		into STRICT	dt_termino_w
		from	cpoe_recomendacao
		where	nr_sequencia = nr_sequencia_p;

	elsif (ie_tipo_item_p = 'D') then

		select	coalesce(cpoe_obter_dt_suspensao(nr_sequencia, 'D'), dt_fim)
		into STRICT	dt_termino_w
		from	cpoe_dialise
		where	nr_sequencia = nr_sequencia_p;

	elsif (ie_tipo_item_p = 'H') then

		select	coalesce(cpoe_obter_dt_suspensao(nr_sequencia, 'H'), dt_fim)
		into STRICT	dt_termino_w
		from	cpoe_hemoterapia
		where	nr_sequencia = nr_sequencia_p;

	elsif (ie_tipo_item_p = 'AP') then

		select	coalesce(cpoe_obter_dt_suspensao(nr_sequencia, 'AP'), dt_fim)
		into STRICT	dt_termino_w
		from	cpoe_anatomia_patologica
		where	nr_sequencia = nr_sequencia_p;

	end if;
end if;


return	coalesce(dt_termino_w,null);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
