SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_tipo_jejum (nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_jejum_w		cpoe_dieta.nr_sequencia%type;
nr_seq_tipo_jejum_w	cpoe_dieta.nr_seq_tipo%type;
ds_tipo_jejum_w		rep_tipo_jejum.ds_tipo%type;


BEGIN

ds_tipo_jejum_w := '';

select	max(nr_sequencia)
into STRICT	nr_seq_jejum_w
from	cpoe_dieta
where	ie_tipo_dieta = 'J'
and		((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = ''))
and     	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	((coalesce(cpoe_obter_dt_suspensao(nr_sequencia,'N')::text, '') = '') or (cpoe_obter_dt_suspensao(nr_sequencia,'N') >= clock_timestamp()))
and (coalesce(dt_fim::text, '') = '' or dt_fim >= clock_timestamp())
and (dt_inicio <= clock_timestamp());

if (nr_seq_jejum_w IS NOT NULL AND nr_seq_jejum_w::text <> '') then
	select 	max(nr_seq_tipo)
	into STRICT 	nr_seq_tipo_jejum_w
	from 	cpoe_dieta
	where	nr_sequencia = nr_seq_jejum_w;

	if coalesce(nr_seq_tipo_jejum_w, 0) <> 0 then
		select	ds_tipo
		into STRICT	ds_tipo_jejum_w
		from	rep_tipo_jejum
		where	nr_sequencia	= nr_seq_tipo_jejum_w;
	end if;
end if;


return	ds_tipo_jejum_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
