CREATE OR REPLACE FUNCTION cpoe_obter_tipo_npt ( cd_pessoa_fisica_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_idade_w						bigint;
qt_idade_dia_w					double precision;
qt_idade_mes_w					double precision;
qt_peso_w						double precision;
ie_tipo_npt_w					varchar(5);
				
c01 CURSOR FOR 
SELECT	IE_TIPO_NPT 
from	CPOE_REGRA_NPT a 
where	qt_idade_w between coalesce(a.qt_idade_min,0) and coalesce(a.qt_idade_max,9999) 
and		qt_idade_dia_w between coalesce(a.qt_idade_min_dia,0) and coalesce(a.qt_idade_max_dia,55000) 
and		qt_idade_mes_w between coalesce(a.qt_idade_min_mes,0) and coalesce(a.qt_idade_max_mes,55000) 
and		coalesce(qt_peso_w,0) between coalesce(qt_peso_min,0) and coalesce(qt_peso_max,999999) 
order	by coalesce(a.qt_idade_min,0), 
		coalesce(a.qt_idade_max,0), 
		coalesce(qt_peso_min,0);
				

BEGIN 
 
select	max(obter_idade(b.dt_nascimento,coalesce(b.dt_obito,clock_timestamp()),'A')), 
		max(obter_idade(b.dt_nascimento,coalesce(b.dt_obito,clock_timestamp()),'DIA')), 
		max(obter_idade(b.dt_nascimento,coalesce(b.dt_obito,clock_timestamp()),'M')) 
into STRICT	qt_idade_w, 
		qt_idade_dia_w, 
		qt_idade_mes_w 
from	pessoa_fisica b 
where	b.cd_pessoa_fisica	= cd_pessoa_fisica_p;
 
if (coalesce(nr_atendimento_p::text, '') = '') then 
	qt_peso_w				:= obter_peso_pf(cd_pessoa_fisica_p);
else 
	qt_peso_w				:= obter_sinal_vital(nr_atendimento_p,'Peso');
end if;
 
open C01;
loop 
fetch C01 into	 
	ie_tipo_npt_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_tipo_npt_w	:= ie_tipo_npt_w;
end loop;
close C01;
 
return	coalesce(ie_tipo_npt_w,'I');
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
