CREATE OR REPLACE FUNCTION cpoe_obter_um_prescr ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_unidade_medida_w	varchar(30);

C01 CURSOR FOR
	SELECT	cd_unidade_medida
	from	material_conversao_unidade
	where	cd_material = cd_material_p
	order by ie_prioridade desc;


BEGIN

select	max(cd_unidade_medida_prescr)
into STRICT	cd_unidade_medida_w
from	material
where	cd_material = cd_material_p;

if (coalesce(cd_unidade_medida_w::text, '') = '') then
	open C01;
	loop
	fetch C01 into
		cd_unidade_medida_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;

return	cd_unidade_medida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
