CREATE OR REPLACE FUNCTION cpoe_obter_valor_status (dt_fim_p timestamp, dt_suspensao_p timestamp default null, dt_lib_suspensao_p timestamp default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(50);
qt_dias_w		bigint;
qt_horas_w		bigint;
data_calculo_w  timestamp;
ds_macro_w			varchar(255);


BEGIN
	ds_retorno_w := '';

	if	(((dt_fim_p IS NOT NULL AND dt_fim_p::text <> '') and (dt_fim_p > clock_timestamp()) and ((coalesce(dt_lib_suspensao_p::text, '') = '') or (dt_suspensao_p > dt_fim_p))) or
		 ((dt_lib_suspensao_p IS NOT NULL AND dt_lib_suspensao_p::text <> '') and (dt_suspensao_p > clock_timestamp()))) then
		
		if ((dt_suspensao_p IS NOT NULL AND dt_suspensao_p::text <> '') and (dt_lib_suspensao_p IS NOT NULL AND dt_lib_suspensao_p::text <> '') and 
			((coalesce(dt_fim_p::text, '') = '') or (dt_fim_p > dt_suspensao_p))) then			
			qt_dias_w := (trunc(dt_suspensao_p) - trunc(clock_timestamp()));
			data_calculo_w := dt_suspensao_p;
		else
			qt_dias_w := (trunc(dt_fim_p) - trunc(clock_timestamp()));
			data_calculo_w := dt_fim_p;
		end if;
		
		if (qt_dias_w < 4) then
			ds_macro_w := 'expire_in=' || qt_dias_w;
			ds_retorno_w := wheb_mensagem_pck.get_texto(1025200, ds_macro_w); -- Expira em Qtd. dias
			
			if (qt_dias_w = 1) then
				ds_retorno_w := wheb_mensagem_pck.get_texto(1025199, ds_macro_w); -- Expira em Qtd. dia
			end if;
		
			if (qt_dias_w = 0) then
				qt_horas_w := round((data_calculo_w - clock_timestamp()) * 1440/60);
				ds_macro_w := 'expire_in=' || qt_horas_w;
				ds_retorno_w := wheb_mensagem_pck.get_texto(1025202, ds_macro_w); --Expira em Qtd. horas
				
				if (qt_horas_w < 2) then
					ds_retorno_w := wheb_mensagem_pck.get_texto(1025201, ds_macro_w); -- Expira em Qtd. hora
				end if;
			end if;
		end if;
	end if;
	
	if	((coalesce(dt_lib_suspensao_p::text, '') = '') and (dt_fim_p <= clock_timestamp())) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(1141794);
	end if;

return	ds_retorno_w;

end;
$body$
