CREATE OR REPLACE FUNCTION cpoe_obter_via_nao_recomendada (cd_material_p bigint, ie_via_aplicacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '' AND ie_via_aplicacao_p IS NOT NULL AND ie_via_aplicacao_p::text <> '') then
		select	coalesce(max(ie_recomendada), 'N')
		into STRICT	ds_retorno_w
		from	mat_via_aplic
		where	cd_material = cd_material_p
		and	ie_via_aplicacao = ie_via_aplicacao_p;
end if;
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
