CREATE OR REPLACE FUNCTION cpoe_ordering_order_unit (nm_tabela_p text,nm_attribute_p text, nr_sequencia_p bigint, nr_seq_cpoe_order_unit_p bigint) RETURNS varchar AS $body$
DECLARE


nr_order_unit_w				cpoe_order_unit.nr_order_unit%type;
nr_seq_prev_order_unit_w	cpoe_order_unit.nr_sequencia%type;
scriptQuery_w				varchar(4000);


BEGIN


scriptQuery_w :=	'select b.nr_seq_prev_order_unit ' ||
					'from '||nm_tabela_p||' a, cpoe_order_unit b '||
					'where a.nr_sequencia = :nr_sequencia_p and a.nr_seq_cpoe_order_unit = b.nr_sequencia '||
					'and exists (select 1 from '||nm_tabela_p||' x '||
					'where x.nr_seq_cpoe_order_unit = b.nr_seq_prev_order_unit '||
					'and x.'||nm_attribute_p||' = a.'||nm_attribute_p||' '||
					'and x.dt_lib_suspensao is not null)';

nr_seq_prev_order_unit_w := obter_valor_dinamico_bv(scriptQuery_w, 'nr_sequencia_p='||nr_sequencia_p||';', nr_seq_prev_order_unit_w, 'S');

select	max(a.nr_order_unit)
into STRICT	nr_order_unit_w
from	cpoe_order_unit a
where	a.nr_sequencia = coalesce(nr_seq_prev_order_unit_w, nr_seq_cpoe_order_unit_p);

return nr_order_unit_w;

end;
$body$
