CREATE OR REPLACE FUNCTION cpoe_overview_obter_status ( ie_tipo_item_p text, dt_recusa_p timestamp, dt_suspensao_p timestamp, dt_inicio_horario_p timestamp, dt_fim_horario_p timestamp) RETURNS varchar AS $body$
BEGIN

if (dt_recusa_p IS NOT NULL AND dt_recusa_p::text <> '') then
	return 'REC';	
elsif (dt_suspensao_p IS NOT NULL AND dt_suspensao_p::text <> '') then
	return 'SUSP';
elsif (dt_inicio_horario_p IS NOT NULL AND dt_inicio_horario_p::text <> '') and (coalesce(dt_fim_horario_p::text, '') = '') then
	return 'INIC';
elsif (dt_fim_horario_p IS NOT NULL AND dt_fim_horario_p::text <> '') then
	return 'ADM';
end if;

return	'PEND';

end;
