CREATE OR REPLACE FUNCTION cpoe_proc_is_group_structure ( nr_seq_reference_p pi_estrutura.nr_seq_estrut_sup%type) RETURNS varchar AS $body$
DECLARE


is_group_structure_w	varchar(1);
	

BEGIN

if (nr_seq_reference_p IS NOT NULL AND nr_seq_reference_p::text <> '') then

	select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
	into STRICT	is_group_structure_w
	from	pi_estrutura
	where	nr_seq_estrut_sup = nr_seq_reference_p
	and		coalesce(si_group_appointment, 'N') = 'S';

end if;

return is_group_structure_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
