CREATE OR REPLACE FUNCTION cpoe_prox_dt_disp_semanal ( dt_reference_p timestamp, si_dispensation_day_p cpoe_week_dispens_rule.si_dispensation_day%type, hr_dispensation_time_p cpoe_week_dispens_rule.hr_dispensation_time%type) RETURNS timestamp AS $body$
DECLARE


dt_disp_week_w		timestamp := null;
dt_disp_w			timestamp;


BEGIN


if ((dt_reference_p IS NOT NULL AND dt_reference_p::text <> '') and (si_dispensation_day_p IS NOT NULL AND si_dispensation_day_p::text <> '') and (hr_dispensation_time_p IS NOT NULL AND hr_dispensation_time_p::text <> '')) then

	dt_disp_w :=  (dt_reference_p + (si_dispensation_day_p - pkg_date_utils.get_weekday(dt_reference_p)));

	dt_disp_week_w := pkg_date_utils.get_DateTime(dt_disp_w, pkg_date_utils.get_Time(hr_dispensation_time_p));

	if (dt_disp_week_w < dt_reference_p) then

		dt_disp_week_w :=  dt_disp_week_w + 7;
		
	end if;

end if;

return dt_disp_week_w;

end;
