CREATE OR REPLACE FUNCTION cpoe_recalcula_horarios_copia ( ds_horarios_p cpoe_material.ds_horarios%type, dt_referencia_p cpoe_material.dt_prox_geracao%type, dt_fim_p cpoe_material.dt_fim%type, ie_acm_p intervalo_prescricao.ie_acm%type, ie_sn_p intervalo_prescricao.ie_se_necessario%type, cd_intervalo_p intervalo_prescricao.cd_intervalo%type ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	cpoe_material.ds_horarios%type;
ds_horarios_w	cpoe_material.ds_horarios%type;
qt_dia_adic_w	integer := 0;
ds_hora_w		varchar(255) := '';
dt_horario_w	timestamp;
ie_operacao_w	intervalo_prescricao.ie_operacao%type;
qt_pertence_w	integer;
i_pos			integer;
sql_w           varchar(2000);


BEGIN
	ds_retorno_w := '';

	if ((dt_fim_p IS NOT NULL AND dt_fim_p::text <> '') and (coalesce(ie_acm_p, 'N') = 'N') and (coalesce(ie_sn_p, 'N') = 'N')) then
		ds_horarios_w := padroniza_horario_prescr(ds_horarios_p, dt_referencia_p);

		select max(a.ie_operacao)
		  into STRICT ie_operacao_w
		  from intervalo_prescricao a
		 where a.cd_intervalo = cd_intervalo_p;

        begin
            sql_w := 'CALL OBTER_RECALCULA_HORARIOS_MD(:1, :2, :3, :4) INTO :ds_retorno_w';
            EXECUTE sql_w USING
                IN dt_referencia_p,
                IN ie_operacao_w,
                IN dt_fim_p,
                IN OUT ds_horarios_w,
                OUT ds_retorno_w;
        exception
            when others then
                ds_retorno_w := '';
        end;
	end if;

	ds_retorno_w := coalesce(replace(padroniza_horario_prescr(ds_retorno_w, dt_referencia_p), 'A', ''), ds_horarios_p);
	return ds_retorno_w;

end;
$body$
